import{_ as s}from"./plugin-vue_export-helper-c27b6911.js";import{o as n,c as i,d,a,e as r}from"./app-fdf13f0b.js";const t="/assets/liunx-ftp-1.png",l="/assets/liunx-ftp-7.png",c="/assets/liunx-ftp-8.png",o="/assets/liunx-ftp-9.png",p="/assets/liunx-ftp-11.png",e="/assets/liunx-ftp-12.png",u="/assets/liunx-ftp-2.png",h="/assets/liunx-ftp-3.png",g="/assets/liunx-ftp-4.png",m="/assets/liunx-ftp-5.png",b="/assets/liunx-ftp-6.png",v="/assets/liunx-ftp-13.png",f="/assets/liunx-ftp-14.png",x="/assets/liunx-ftp-15.png",_="/assets/liunx-ftp-16.png",k="/assets/liunx-ftp-17.png",F="/assets/liunx-ftp-18.png",P="/assets/liunx-ftp-19.png",T="/assets/liunx-ftp-20.png",y="/assets/liunx-ftp-21.png",E="/assets/liunx-ftp-24.png",z="/assets/liunx-ftp-25.png",S="/assets/liunx-ftp-23.png",Y={},w=a("div",{class:"hint-container tip"},[a("p",{class:"hint-container-title"},"前言"),a("p",null,"本篇是 Liunx 的服务配置笔记系列第三篇, 这一篇将具体介绍 Red Hat Liunx 下 FTP 服务的安全配置。确切的说, 应该叫它 vsFTP, 就是文件的上传和下载服务, 主要的功能是在服务器与客户端之间进行文件的传输。")],-1),C=r(`<h2 id="配置文件的位置" tabindex="-1"><a class="header-anchor" href="#配置文件的位置" aria-hidden="true">#</a> 配置文件的位置</h2><p>主配置文件: <strong>/etc/vsftpd/vsftpd.conf</strong></p><h2 id="服务的启动与停止" tabindex="-1"><a class="header-anchor" href="#服务的启动与停止" aria-hidden="true">#</a> 服务的启动与停止</h2><div class="hint-container tip"><p class="hint-container-title">提示</p><p>FTP 的服务名并不叫做 <code>vsftp</code> , 而是 <code>vsftpd</code> 。<br> 修改配置文件过后一定要重启 <code>vsftpd</code> 服务。</p></div><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">service</span> vsftpd status        <span class="token comment"># 状态</span>
<span class="token function">service</span> vsftpd start         <span class="token comment"># 启动</span>
<span class="token function">service</span> vsftpd stop          <span class="token comment"># 暂停</span>
<span class="token function">service</span> vsftpd restart       <span class="token comment"># 重启</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="服务的安装" tabindex="-1"><a class="header-anchor" href="#服务的安装" aria-hidden="true">#</a> 服务的安装</h2><h3 id="创建挂载目录" tabindex="-1"><a class="header-anchor" href="#创建挂载目录" aria-hidden="true">#</a> 创建挂载目录</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mkdir</span> <span class="token parameter variable">-p</span> /mnt/cdrom
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="挂载光盘" tabindex="-1"><a class="header-anchor" href="#挂载光盘" aria-hidden="true">#</a> 挂载光盘</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mount</span> /dev/cdrom /mnt/cdrom
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="安装" tabindex="-1"><a class="header-anchor" href="#安装" aria-hidden="true">#</a> 安装</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">cd</span> /mnt/cdrom/Server
<span class="token function">rpm</span> <span class="token parameter variable">-ivh</span> vsftpd-2.0.5-16.e15i386.rpm
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><figure><img src="`+t+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h2 id="安全配置" tabindex="-1"><a class="header-anchor" href="#安全配置" aria-hidden="true">#</a> 安全配置</h2><h3 id="禁止匿名用户登录" tabindex="-1"><a class="header-anchor" href="#禁止匿名用户登录" aria-hidden="true">#</a> 禁止匿名用户登录</h3><p><code>Default Configuration:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token assign-left variable">anonymous_enable</span><span class="token operator">=</span>YES
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>默认的值是允许的, 这样匿名用户就可以不需要密码就可以登录 FTP 了, 这样很不安全, 把它修改成 NO 吧。</p><figure><img src="`+l+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="使用客户端进行验证" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。我们就直接使用 FTP 用户登录吧。</p><p><code>Connection Format:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ftp</span> <span class="token punctuation">[</span>host<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>若没有成功登录, 则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">530</span> Login incorrect.
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>使用 <code>bye</code> , 可以退出 FTP, 成功则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">221</span> Googbye
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+c+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="使用户不能登录系统-但可以登录-ftp" tabindex="-1"><a class="header-anchor" href="#使用户不能登录系统-但可以登录-ftp" aria-hidden="true">#</a> 使用户不能登录系统, 但可以登录 FTP</h3><p>首先要创建一个普通的用户, 使他不能登录系统, 只需设置登录的 shell 就好了。</p><p>创建一个新用户, 别忘了设置密码, <code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">useradd</span> <span class="token parameter variable">-s</span> /sbin/nologin <span class="token builtin class-name">test</span>
<span class="token function">passwd</span> <span class="token builtin class-name">test</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p><code>For Example:</code> <br></p><figure><img src="`+o+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="使用客户端进行验证-1" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-1" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。这时候, 我们就要使用之前创建的用户进行验证了。</p><p>若登录成功, 则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">230</span> Login successful
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+p+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="禁止用户写入或者上传文件" tabindex="-1"><a class="header-anchor" href="#禁止用户写入或者上传文件" aria-hidden="true">#</a> 禁止用户写入或者上传文件</h3><p><code>Default Configuration:</code> <br></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token assign-left variable">write_enable</span><span class="token operator">=</span>YES
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>默认是允许用户进行上传或者写入文件, 我们可以根据需求进行修改。</p><figure><img src="`+e+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="使用客户端进行验证-2" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-2" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。这时候, 我们仍然使用之前创建的用户进行验证。</p><p>无法创建目录, 则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">550</span> Permission denied
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>尝试切换目录, 成功则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">250</span> Directory successfully changed.
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+e+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="配置空闲会话中断时间" tabindex="-1"><a class="header-anchor" href="#配置空闲会话中断时间" aria-hidden="true">#</a> 配置空闲会话中断时间</h3><p><code>Default Configuration:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token comment">#idle_session_timeout=600</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>默认的配置时间是十分钟, （默认的单位为秒）, 我们可以根据需求进行修改。</p><figure><img src="`+u+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="使用客户端进行验证-3" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-3" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。成功登录之后, 我们等待一段时间, 在进行操作。</p><p>若超时, 则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">421</span> Timeout
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+h+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="配置数据连接超时时间" tabindex="-1"><a class="header-anchor" href="#配置数据连接超时时间" aria-hidden="true">#</a> 配置数据连接超时时间</h3><p><code>Default Configuration:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token comment">#data_connection_timeout=120</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>默认的配置是两分钟, （默认的单位为秒）, 我们可以根据需求进行修改。</p><figure><img src="`+g+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="使用客户端进行验证-4" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-4" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>此选项需要在传输数据的时候可用, 这里不再进行演示。</p><h3 id="限制普通用户只能在家目录中" tabindex="-1"><a class="header-anchor" href="#限制普通用户只能在家目录中" aria-hidden="true">#</a> 限制普通用户只能在家目录中</h3><p><code>Default Configuration:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token comment">#chroot_list_enable=YES</span>
<span class="token comment">#chroot_list_file=/etc/vsftpd/chroot_list</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>默认是允许用户跳出自己的家目录的, 我们可以限制他只能在自己的家目录里。注意, 要将用户限制在家目录中, 还需要设置 &#39;chroot_local_user=YES&#39;。</p><figure><img src="`+m+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><p>别忘了将禁止的用户添加至 <code>/etc/vsftpd/chroot_list</code></p><h4 id="使用客户端进行验证-5" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-5" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。这时候, 我们仍然使用之前创建的用户进行验证。</p><p>不能切换目录, 则会显示</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token number">550</span> Failed to changed directory.
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+b+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="设置黑名单" tabindex="-1"><a class="header-anchor" href="#设置黑名单" aria-hidden="true">#</a> 设置黑名单</h3><p><code>Default Configuration:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token assign-left variable">userlist_enable</span><span class="token operator">=</span>YES
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>默认情况下就是开启的, 若无开启, 我们可以根据需求进行修改。</p><figure><img src="`+v+'" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><p>别忘了, 将禁止的用户填入 <code>/etc/vsftpd/ftpusers</code></p><h4 id="使用客户端进行验证-6" tabindex="-1"><a class="header-anchor" href="#使用客户端进行验证-6" aria-hidden="true">#</a> 使用客户端进行验证</h4><p>验证之前, 请重启服务。这时候, 我们新建一个用户, 然后将其加入黑名单, 再进行验证。</p><p>新建用户, <code>For Example:</code> <br></p><figure><img src="'+f+'" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><p>添加至黑名单, <code>For Example:</code> <br></p><figure><img src="'+x+'" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><figure><img src="'+_+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h2 id="配置虚拟用户登录" tabindex="-1"><a class="header-anchor" href="#配置虚拟用户登录" aria-hidden="true">#</a> 配置虚拟用户登录</h2><h3 id="先安装-db4" tabindex="-1"><a class="header-anchor" href="#先安装-db4" aria-hidden="true">#</a> 先安装 db4</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">rpm</span> <span class="token parameter variable">-ivh</span> db4-utils-4.3.29-10.el5.i386.rpm
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="`+k+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="生成虚拟用户口令库文件" tabindex="-1"><a class="header-anchor" href="#生成虚拟用户口令库文件" aria-hidden="true">#</a> 生成虚拟用户口令库文件</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">vim</span> /etc/vsftpd/login.txt
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>需要注意的是, 该文件的格式如下, 单数行为用户名, 偶数行为口令。</p><figure><img src="`+F+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="生成口令库文件-并修改其权限" tabindex="-1"><a class="header-anchor" href="#生成口令库文件-并修改其权限" aria-hidden="true">#</a> 生成口令库文件, 并修改其权限</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>db_load <span class="token parameter variable">-T</span> <span class="token parameter variable">-t</span> <span class="token builtin class-name">hash</span> <span class="token parameter variable">-f</span> /etc/vsftpd/login.txt /etc/vsftpd/login.db
<span class="token function">chmod</span> <span class="token number">600</span> /etc/vsftpd/login.db
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><p>注意要设置权限, 只有 root 用户拥有读写权限。</p><figure><img src="`+P+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h3 id="修改虚拟用户的-pam-文件" tabindex="-1"><a class="header-anchor" href="#修改虚拟用户的-pam-文件" aria-hidden="true">#</a> 修改虚拟用户的 PAM 文件</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">vim</span> /etc/pam.d/vsftpd
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>加入如下内容, 其他的全部注释掉</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>auth required pam_userdb.so <span class="token assign-left variable">db</span><span class="token operator">=</span>/etc/vsftpd/login
account required pam_userdb.so <span class="token assign-left variable">db</span><span class="token operator">=</span>/etc/vsftpd/login
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><figure><img src="`+T+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><p>数据文件的地址, 记得要写对哦。</p><h3 id="建立虚拟用户-并设置权限" tabindex="-1"><a class="header-anchor" href="#建立虚拟用户-并设置权限" aria-hidden="true">#</a> 建立虚拟用户, 并设置权限</h3><p><code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">useradd</span> <span class="token parameter variable">-d</span> /home/ftp <span class="token parameter variable">-s</span> /sbin/nologin virtual
<span class="token function">chmod</span> <span class="token number">777</span> /home/ftp
<span class="token function">passwd</span> virtual
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><figure><img src="`+y+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="修改主配置文件" tabindex="-1"><a class="header-anchor" href="#修改主配置文件" aria-hidden="true">#</a> 修改主配置文件</h4><p>添加如下内容, <code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token assign-left variable">guest_enable</span><span class="token operator">=</span>YES
<span class="token assign-left variable">guest_username</span><span class="token operator">=</span>virtual
<span class="token assign-left variable">user_config_dir</span><span class="token operator">=</span>/etc/vsftpd_user_conf
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><figure><img src="`+E+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><h4 id="创建配置文件存放目录" tabindex="-1"><a class="header-anchor" href="#创建配置文件存放目录" aria-hidden="true">#</a> 创建配置文件存放目录</h4><p>此目录一定要与配置文件中的一致, <code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mkdir</span> <span class="token parameter variable">-p</span> /etc/vsftpd_user_conf
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h4 id="为虚拟用户创建配置文件" tabindex="-1"><a class="header-anchor" href="#为虚拟用户创建配置文件" aria-hidden="true">#</a> 为虚拟用户创建配置文件</h4><p>为虚拟口令库中的每个用户分别创建一个配置文件, 配置文件中的配置于匿名用户的配置相差无几。</p><p>先创建配置文件, <code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">vim</span> /etc/vsftpd_user_conf/ftp_test
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>在配置文件中配置如下内容,</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token assign-left variable">anon_world_readable_only</span><span class="token operator">=</span>NO               <span class="token comment"># 是否允许浏览 FTP 目录和下载</span>
<span class="token assign-left variable">anon_upload_enable</span><span class="token operator">=</span>YES                    <span class="token comment"># 是否允许上传</span>
<span class="token assign-left variable">write_enable</span><span class="token operator">=</span>YES                          <span class="token comment"># 是否有写权限</span>
<span class="token assign-left variable">download_enable</span><span class="token operator">=</span>YES                       <span class="token comment"># 是否允许下载文件</span>
<span class="token assign-left variable">anon_mkdir_write_enable</span><span class="token operator">=</span>YES               <span class="token comment"># 是否允许建立和删除目录</span>
<span class="token assign-left variable">anon_other_write_enable</span><span class="token operator">=</span>YES               <span class="token comment"># 是否允许改名和删除文件</span>
<span class="token assign-left variable">local_root</span><span class="token operator">=</span>/var/ftp/share/123
<span class="token comment"># 指定虚拟用户在系统用户下面的路径,限制虚拟用户的家目录,虚拟用户登录后的主目录。</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><figure><img src="`+z+`" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure><p>最后只需创建一下虚拟用户登录后的主目录, <code>For Example:</code></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mkdir</span> <span class="token parameter variable">-p</span> /var/ftp/share/123
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><h3 id="登录测试" tabindex="-1"><a class="header-anchor" href="#登录测试" aria-hidden="true">#</a> 登录测试</h3><p>这时候就要使用虚拟用户口令库文件里的用户名和密码来登录 FTP 服务器</p><figure><img src="`+S+'" alt="FTP 安全配置" tabindex="0" loading="lazy"><figcaption>FTP 安全配置</figcaption></figure>',140);function D(N,L){return n(),i("div",null,[w,d(" more "),C])}const q=s(Y,[["render",D],["__file","liunx-service-ftp.html.vue"]]);export{q as default};
