import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as l,c as a,d as e}from"./app.a3d402e1.js";const t={},c=e('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55 PMP \u8003\u8BD5\u7684\u4E00\u4E9B\u89E3\u9898\u601D\u8DEF</p></div><h2 id="\u53D8\u66F4\u9898" tabindex="-1"><a class="header-anchor" href="#\u53D8\u66F4\u9898" aria-hidden="true">#</a> \u53D8\u66F4\u9898</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u5F53\u9898\u5E72\u4E2D\u51FA\u73B0\uFF1A\u65B0\u7684\u3001\u989D\u5916\u7684\u3001\u8BBE\u5907\u53D8\u5316\u3001\u5DE5\u4F5C\u9057\u6F0F\u3001\u8303\u56F4\u8513\u5EF6\u3001\u786E\u4FDD\u7F3A\u9677\u8865\u6551\u6B63\u786E\u3001\u4E89\u8BBA\u8981\u4E0D\u8981\u53D8\u66F4\u3001\u7B49\u7B49\u5747\u5C5E\u4E8E\u53D8\u66F4\u9898\u3002</p></div><ul><li>\u53D8\u66F4\u7684\u672C\u8D28\u662F\u5BF9\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\uFF08\u57FA\u51C6\uFF09\u3001\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u4FEE\u6539</li><li>\u53D8\u66F4\u5F80\u5F80\u662F\u51FA\u4E8E\u67D0\u79CD\u9700\u8981, \u65E0\u6240\u8C13\u597D\u4E0E\u4E0D\u597D\uFF1B\u4E0D\u8981\u770B\u6210\u662F\u95EE\u9898, \u4E5F\u4E0D\u8981\u770B\u6210\u662F\u98CE\u9669\uFF1B</li><li>\u6211\u4EEC\u66F4\u591A\u7684\u662F\u5173\u6CE8\u53D8\u66F4\u7684\u53D7\u63A7, \u8BC4\u4F30\u5BF9\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\uFF08\u57FA\u51C6\uFF09\u7684\u5F71\u54CD, \u800C\u4E0D\u662F\u201C\u62C5\u5FC3\u201D\u5E26\u6765\u7684\u95EE\u9898\u6216\u98CE\u9669</li><li>\u5BF9\u5F85\u53D8\u66F4\u7684\u6001\u5EA6\uFF1A\u65E0\u6240\u8C13\u201C\u6B22\u8FCE\u201D\u8FD8\u662F\u201C\u4E0D\u6B22\u8FCE\u201D, \u9700\u8981\u53D8\u66F4\u5C31\u53D8\u66F4, \u4F46\u662F\u8981\u53D7\u63A7</li></ul><p>\u53D8\u66F4\u6D41\u7A0B\uFF1A\uFF08\u4E86\u89E3\u53D8\u66F4\u7684\u5177\u4F53\u5185\u5BB9\u6216\u539F\u56E0, \u544A\u77E5\u53D8\u66F4\u7684\u6D41\u7A0B, \u907F\u514D\u4E0D\u5FC5\u8981\u7684\u53D8\u66F4\uFF09</p><p>1.\u8BB0\u5F55\uFF08\u9879\u76EE\u7ECF\u7406\u4E66\u9762\u8BB0\u5F55\u6216\u8981\u6C42\u53D8\u66F4\u63D0\u51FA\u8005\u63D0\u4EA4\u4E66\u9762\u7684\u53D8\u66F4\uFF092.\u8BC4\u4F30, 3.\u63D0\u4EA4\uFF08\u9879\u76EE\u7ECF\u7406\u63D0\u4EA4\u7ED9 CCB\uFF09, 4.\u66F4\u65B0, 5.\u901A\u77E5</p><p>\u5982\u679C\u83B7\u5F97\u6279\u51C6, \u5C31\u5FC5\u987B\u8981\u53BB\u6267\u884C\u6279\u51C6\u7684\u53D8\u66F4, \u5982\u679C\u5176\u4ED6\u76F8\u5173\u65B9\u6709\u610F\u89C1\u53EF\u4EE5\u91CD\u65B0\u63D0\u4EA4\u65B0\u7684\u53D8\u66F4 \u5982\u679C\u53D8\u66F4\u6CA1\u6709\u83B7\u5F97\u6279\u51C6\u5C31\u76F4\u63A5\u6267\u884C\u4E86, \u5219\u9700\u8981\uFF1A1.\u505C\u6B62\u4E0D\u826F\u53D8\u66F4\uFF08\u4E0D\u662F\u505C\u6B62\u6574\u4E2A\u9879\u76EE\uFF09, 2. \u8865\u53D8\u66F4\u6D41\u7A0B\u3002</p><h2 id="\u76F8\u5173\u65B9\u4E0E\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u76F8\u5173\u65B9\u4E0E\u6C9F\u901A" aria-hidden="true">#</a> \u76F8\u5173\u65B9\u4E0E\u6C9F\u901A</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u76F8\u5173\u65B9\u7684\u7BA1\u7406\u5728\u4E8E\u7BA1\u7406\u4EBA</p><p>\u9047\u5230\u76F8\u5173\u65B9\u53CD\u5BF9\u3001\u62C5\u5FE7\u3001\u62B5\u5236\u3001\u5A01\u80C1\u3001\u4E0D\u652F\u6301\u3001\u4E0D\u53C2\u4E0E, \u4E8B\u540E\u63D0\u51FA\u4E0D\u540C\u610F\u89C1\u7B49\u7B49, \u5747\u5C5E\u4E8E\u76F8\u5173\u65B9\u9898</p></div><ul><li>\u5982\u679C\u4E4B\u524D\u6CA1\u6709\u8BC6\u522B\u8BE5\u76F8\u5173\u65B9\uFF1A\u66F4\u65B0\u76F8\u5173\u65B9\u767B\u8BB0\u518C, \u505A\u597D\u76F8\u5173\u65B9\u5206\u6790\u548C\u5206\u7C7B\uFF08\u6743\u529B\u5229\u76CA\uFF09</li><li>\u5982\u679C\u5DF2\u7ECF\u8BC6\u522B\u4E86\u8BE5\u76F8\u5173\u65B9\uFF1A\u5BA1\u67E5\u76F8\u5173\u65B9\u53C2\u4E0E\u8BA1\u5212, \u6216\u8005, \u7BA1\u7406\u76F8\u5173\u65B9\u53C2\u4E0E\uFF08\u5F15\u5BFC\u53C2\u4E0E, \u83B7\u5F97\u652F\u6301\uFF1B\u8C08\u5224\u6C9F\u901A, \u7BA1\u7406\u671F\u671B\uFF1B\u5904\u7406\u98CE\u9669, \u9884\u6D4B\u95EE\u9898\uFF1B\u6F84\u6E05\u548C\u89E3\u51B3\u5DF2\u8BC6\u522B\u7684\u95EE\u9898\uFF09</li><li>\u5982\u679C\u95EE\u4E8B\u5148\u8981\u505A\u597D\u4EC0\u4E48\uFF1A\u65E9\u8BC6\u522B\u3001\u65E9\u5206\u6790\uFF08\u76F8\u5173\u65B9\u5206\u6790\uFF09\u3001\u65E9\u53C2\u4E0E</li></ul><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u6C9F\u901A\u7684\u7BA1\u7406\u5728\u4E8E\u7BA1\u7406\u4FE1\u606F\u4F20\u9012</p><p>\u4FE1\u606F\u6D41\u4F20\u9012\u7684\u95EE\u9898\u624D\u662F\u6C9F\u901A\u95EE\u9898\uFF1A\u591A\u53D1\u5C11\u53D1, \u6CA1\u6709\u53CA\u65F6\u53D1, \u770B\u4E0D\u61C2, \u4E0D\u662F\u60F3\u8981\u7684, \u4E0D\u65B9\u4FBF\u9605\u8BFB, \u865A\u62DF\u56E2\u961F\u7684\u6C9F\u901A\u95EE\u9898, \u76F8\u5173\u65B9\u62B1\u5728\u6028\u6C9F\u901A\u4E0D\u53CA\u65F6, \u865A\u62DF\u56E2\u961F\u7684\u8BEF\u89E3, \u5747\u662F\u6C9F\u901A\u9898</p></div><ul><li>\u5E38\u89C1\u7684\u9009\u9879\u6709\uFF1A\u5BA1\u67E5\u7BA1\u7406\u8BA1\u5212\u3001\u66F4\u65B0\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212\u3001\u521B\u5EFA\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212, \u4EE5\u53CA\u8DDF\u89C4\u5212\u6C9F\u901A\u6709\u5173\u7684\u5DE5\u5177\uFF1A\u6C9F\u901A\u9700\u6C42\u5206\u6790, \u6C9F\u901A\u98CE\u683C\u8BC4\u4F30\u7B49\u7B49</li><li>\u6CE8\u610F\u533A\u5206, \u662F\u8981\u4F60\u7BA1\u4EBA, \u8FD8\u662F\u8981\u4F60\u7BA1\u4FE1\u606F\u7684\u4F20\u9012\uFF1B\u4EBA\u7684\u95EE\u9898\u5C5E\u4E8E\u76F8\u5173\u65B9\u77E5\u8BC6\u9886\u57DF\u3001\u4FE1\u606F\u4F20\u9012\u7684\u95EE\u9898\u5C5E\u4E8E\u6C9F\u901A\u77E5\u8BC6\u9886\u57DF</li></ul><h2 id="\u98CE\u9669\u548C\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u98CE\u9669\u548C\u95EE\u9898" aria-hidden="true">#</a> \u98CE\u9669\u548C\u95EE\u9898</h2><ul><li>\u98CE\u9669\u662F\u4E00\u79CD\u4E0D\u786E\u5B9A\u6027\u7684\u4E8B\u4EF6, \u6709\u6982\u7387\u53D1\u751F, \u53D1\u751F\u4E4B\u540E\u4F1A\u5BF9\u9879\u76EE\u4EA7\u751F\u79EF\u6781\u6216\u6D88\u6781\u7684\u5F71\u54CD</li><li>\u95EE\u9898\u662F\u5F53\u524D\u5BA2\u89C2\u5B58\u5728\u7684\u60C5\u5F62, \u5DF2\u7ECF\u5BF9\u9879\u76EE\u4EA7\u751F\u4E86\u5F71\u54CD</li><li>\u5BF9\u4E8E\u98CE\u9669\u6765\u8BF4\uFF1A1. \u672A\u6765\u53EF\u80FD\u53D1\u751F, 2.\u4E00\u65E6\u53D1\u751F\u5C31\u4F1A\u6709\u5F71\u54CD</li><li>\u5BF9\u4E8E\u95EE\u9898\u6765\u8BF4\uFF1A1. \u5F53\u524D\u5BA2\u89C2\u5B58\u5728, 2.\u5DF2\u7ECF\u5BF9\u9879\u76EE\u4EA7\u751F\u5F71\u54CD</li></ul><div class="custom-container tip"><p class="custom-container-title">\u98CE\u9669\u7684\u9898\u76EE</p><ul><li>\u98CE\u9669\u7684\u9898\u76EE\uFF08\u5927\u591A\u6570\u98CE\u9669\u9898, \u9898\u5E72\u4E2D\u4F1A\u660E\u786E\u8BF4\u5230\u201C\u53EF\u80FD\u201D\u5E26\u6765\u7684\u5F71\u54CD\uFF09</li><li>\u8BC6\u522B\u5230\u98CE\u9669\uFF1A\u66F4\u65B0\uFF08\u67E5\u9605\uFF09\u98CE\u9669\u767B\u8BB0\u518C\u3001\u5B9A\u6027\u3001\u5B9A\u91CF\u3001\u89C4\u5212\u5E94\u5BF9\u3001\u5B9E\u65BD\u5E94\u5BF9</li><li>\u53D1\u751F\u4E86\u98CE\u9669\uFF1A\u5DF2\u77E5\u98CE\u9669, \u67E5\u9605\u98CE\u9669\u767B\u8BB0\u518C\u76F4\u63A5\u5E94\u5BF9, \u5982\u679C\u8981\u7528\u50A8\u5907, \u201C\u5E94\u6025\u50A8\u5907\u201D\uFF1B\u672A\u77E5\u98CE\u9669\uFF1A\u6743\u53D8, \u8D70\u53D8\u66F4\u6D41\u7A0B, \u7528\u7BA1\u7406\u50A8\u5907</li></ul></div><div class="custom-container tip"><p class="custom-container-title">\u95EE\u9898\u7684\u9898\u76EE</p><ul><li>\u8BB0\u5F55\u95EE\u9898\u65E5\u5FD7, \u5206\u6790\u6839\u672C\u539F\u56E0\uFF08\u6216\u8BC4\u4F30\u5F71\u54CD\uFF09, \u786E\u5B9A\u89E3\u51B3\u65B9\u6848\u3002</li><li>\u95EE\u9898\u89E3\u51B3\u7684\u5B8C\u6574\u6B65\u9AA4\uFF1A1.\u5B9A\u4E49, 2.\u8BC6\u522B, 3.\u65B9\u6848, 4.\u9009\u62E9, 5.\u6267\u884C, 6.\u9A8C\u8BC1</li></ul></div><h2 id="\u539F\u56E0\u548C\u6839\u672C\u539F\u56E0" tabindex="-1"><a class="header-anchor" href="#\u539F\u56E0\u548C\u6839\u672C\u539F\u56E0" aria-hidden="true">#</a> \u539F\u56E0\u548C\u6839\u672C\u539F\u56E0</h2><div class="custom-container tip"><p class="custom-container-title">\u627E\u539F\u56E0</p><p>\u5982\u679C\u9898\u76EE\u63CF\u8FF0\u7684\u662F\u60F3\u8981\u627E\u539F\u56E0, \u95EE\u9898\u662F\u4F7F\u7528\u4EC0\u4E48\u5DE5\u5177\u548C\u6280\u672F, \u9009\u9879\u627E\uFF1A<code>\u6839\u672C\u539F\u56E0\u5206\u6790</code></p></div><div class="custom-container tip"><p class="custom-container-title">\u627E\u6839\u672C\u539F\u56E0</p><p>\u5982\u679C\u9898\u76EE\u63CF\u8FF0\u7684\u662F\u60F3\u8981\u627E\u6839\u672C\u539F\u56E0, \u95EE\u9898\u662F\u5982\u4F55\u627E\u6839\u672C\u539F\u56E0, \u9009\u9879\u627E\uFF1A<code>\u9C7C\u9AA8\u56FE</code>\u3001<code>\u77F3\u5DDD\u56FE</code>\u3001<code>\u56E0\u679C\u56FE</code></p></div><h2 id="\u6B63\u5E38\u6536\u5C3E\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u6B63\u5E38\u6536\u5C3E\u6D41\u7A0B" aria-hidden="true">#</a> \u6B63\u5E38\u6536\u5C3E\u6D41\u7A0B</h2><ol><li>\u83B7\u5F97\u9879\u76EE\u53D1\u8D77\u4EBA\u548C\u76F8\u5173\u65B9\u7684\u6279\u51C6\uFF08\u9A8C\u6536\uFF09</li><li>\u79FB\u4EA4\u6700\u7EC8\u6210\u679C</li><li>\u603B\u7ED3\u7ECF\u9A8C\u6559\u8BAD, \u63D0\u4F9B\u672A\u6765\u9879\u76EE\u6216\u9636\u6BB5\u4F7F\u7528</li><li>\u66F4\u65B0\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7</li><li>\u6587\u4EF6\u5F52\u6863</li><li>\u91CA\u653E\u8D44\u6E90\u3001\u89E3\u6563\u9879\u76EE\u56E2\u961F</li><li>\u8861\u91CF\u5E76\u8BB0\u5F55\u5BA2\u6237\u6EE1\u610F\u5EA6</li><li>\u5E86\u529F\u4F1A</li></ol><h2 id="\u63D0\u524D\u6536\u5C3E\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u63D0\u524D\u6536\u5C3E\u6D41\u7A0B" aria-hidden="true">#</a> \u63D0\u524D\u6536\u5C3E\u6D41\u7A0B</h2><ol><li>\u5728\u6B63\u5F0F\u7684\u6536\u5C3E\u6587\u4EF6\u4E2D\u8BF4\u660E\u9879\u76EE\u7EC8\u6B62\u7684\u539F\u56E0</li><li>\u628A\u8BE5\u9879\u76EE\u7684\u5DF2\u5B8C\u6210\u548C\u672A\u5B8C\u6210\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u79FB\u4EA4\u4ED6\u4EBA</li><li>\u603B\u7ED3\u7ECF\u9A8C\u6559\u8BAD, \u63D0\u4F9B\u672A\u6765\u9879\u76EE\u6216\u9636\u6BB5\u4F7F\u7528</li><li>\u66F4\u65B0\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7</li><li>\u6587\u4EF6\u5F52\u6863</li><li>\u91CA\u653E\u8D44\u6E90\u3001\u89E3\u6563\u9879\u76EE\u56E2\u961F</li></ol>',23),s=[c];function o(n,r){return l(),a("div",null,s)}var h=i(t,[["render",o],["__file","problem-solving.html.vue"]]);export{h as default};
