import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as i,d as t}from"./app.a3d402e1.js";var r="/assets/project-communication-management.png",n="/assets/project-communication-management-0.png",c="/assets/project-communication-management-1.png",h="/assets/project-communication-management-2.png",o="/assets/project-communication-management-3.png";const p={},s=t('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55\u9879\u76EE\u6C9F\u901A\u7BA1\u7406\u7AE0\u8282\u7684\u76F8\u5173\u77E5\u8BC6\u3001\u9891\u7E41\u8003\u70B9\u3002</p></div><h2 id="\u6838\u5FC3\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u6982\u5FF5" aria-hidden="true">#</a> \u6838\u5FC3\u6982\u5FF5</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u6C9F\u901A\u7BA1\u7406\u5C31\u662F\u5728\u5408\u9002\u7684\u65F6\u95F4\u8BA9\u5408\u9002\u7684\u4EBA\u901A\u8FC7\u5408\u9002\u7684\u65B9\u5F0F\u628A\u5408\u9002\u7684\u4FE1\u606F\u4F20\u8FBE\u7ED9\u5408\u9002\u7684\u4EBA, \u770B\u4F3C\u4E00\u53E5\u65E0\u7528\u7684\u8BDD, \u4F46\u5982\u4F55\u9009\u62E9\u201C\u5408\u9002\u201D\u7684\u4EBA\u3001\u201C\u5408\u9002\u201D\u7684\u65B9\u5F0F\u975E\u5E38\u8003\u9A8C\u4EBA, \u6240\u4EE5\u6C9F\u901A\u7BA1\u7406\u80FD\u529B\u662F\u9879\u76EE\u7ECF\u7406\u6700\u91CD\u8981\u7684\u80FD\u529B\u4E4B\u4E00\u3002</p></div><p>\u6C9F\u901A =&gt; \u901A\u8FC7\u6C9F\u901A\u6D3B\u52A8\uFF08\u5982\u4F1A\u8BAE\u548C\u6F14\u8BB2\uFF09, \u6216\u5DF2\u5DE5\u4F5C\u7684\u65B9\u5F0F\uFF08\u5982\u7535\u5B50\u90AE\u4EF6\u3001\u793E\u4EA4\u5A92\u4F53\u3001\u9879\u76EE\u62A5\u544A\u6216\u9879\u76EE\u6587\u6863\uFF09\u7B49\u5404\u79CD\u53EF\u80FD\u7684\u65B9\u5F0F\u6765\u53D1\u9001\u6216\u8005\u63A5\u6536\u4FE1\u606F\u3002</p><p>\u6C9F\u901A\u6D3B\u52A8\u53EF\u6309\u591A\u79CD\u7EF4\u5EA6\u8FDB\u884C\u5206\u7C7B, \u5305\u62EC\uFF1A \u5185\u90E8 / \u5916\u90E8\uFF1B\u6B63\u5F0F / \u975E\u6B63\u5F0F\uFF1B\u5C42\u7EA7\u6C9F\u901A\uFF08\u5411\u4E0A\u6C9F\u901A\u3001\u5411\u4E0B\u6C9F\u901A\u3001\u6A2A\u5411\u6C9F\u901A\uFF09\uFF1B\u5B98\u65B9\u6C9F\u901A /\u975E\u5B98\u65B9\u6C9F\u901A\uFF1B\u4E66\u9762 / \u53E3\u5934\u6C9F\u901A\u3002</p><p>\u6210\u529F\u7684\u6C9F\u901A\u5305\u62EC\u4E24\u4E2A\u90E8\u5206\uFF1A</p><ol><li>\u6839\u636E\u9879\u76EE\u53CA\u5176\u76F8\u5173\u65B9\u7684\u9700\u6C42\u800C\u5236\u5B9A\u9002\u5F53\u7684\u6C9F\u901A\u7B56\u7565\u3002</li><li>\u5236\u5B9A\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212, \u786E\u4FDD\u7528\u5404\u79CD\u5F62\u5F0F\u548C\u624B\u6BB5\u628A\u6070\u5F53\u7684\u4FE1\u606F\u4F20\u9012\u7ED9\u76F8\u5173\u65B9\u3002</li><li>5C \u539F\u5219\uFF1ACorrect\uFF1A\u6B63\u786E\u7684\u8BED\u6CD5\u548C\u62FC\u5199\uFF1B Concise\uFF1A\u7B80\u6D01\u7684\u8868\u8FF0\u548C\u65E0\u591A\u4F59\u5B57 \uFF1BClear\uFF1A\u6E05\u6670\u7684\u76EE\u7684\u548C\u8868\u8FF0\uFF08\u9002\u5408\u8BFB\u8005\u7684\u9700\u8981\uFF09\uFF1BCoherent\uFF1A\u8FDE\u8D2F\u7684\u601D\u7EF4\u903B\u8F91\uFF1B Controlling\uFF1A\u53D7\u63A7\u7684\u8BED\u53E5\u548C\u60F3\u6CD5\u627F\u63A5</li></ol><p>\u5728\u654F\u6377\u6216\u9002\u5E94\u578B\u73AF\u5883\u4E2D\u9700\u8003\u8651\u7684\u56E0\u7D20</p><ol><li>\u7B80\u5316\u56E2\u961F\u6210\u5458\u83B7\u53D6\u4FE1\u606F\u7684\u901A\u9053</li><li>\u9891\u7E41\u8FDB\u884C\u56E2\u961F\u68C0\u67E5</li><li>\u8BA9\u56E2\u961F\u6210\u5458\u96C6\u4E2D\u529E\u516C</li><li>\u4EE5\u900F\u660E\u7684\u65B9\u5F0F\u53D1\u5E03\u9879\u76EE\u5DE5\u4F5C, \u8BA9\u9886\u5BFC\u548C\u76F8\u5173\u65B9\u6307\u5BFC</li><li>\u5B9A\u671F\u9080\u8BF7\u76F8\u5173\u65B9\u8BC4\u5BA1\u9879\u76EE\u5DE5\u4F5C</li></ol><h2 id="\u601D\u7EF4\u5BFC\u56FE" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u5BFC\u56FE" aria-hidden="true">#</a> \u601D\u7EF4\u5BFC\u56FE</h2><p><img src="'+r+'" alt="\u9879\u76EE\u6C9F\u901A\u7BA1\u7406"></p><h2 id="\u5982\u4F55\u505A\u597D\u9879\u76EE\u6C9F\u901A\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u505A\u597D\u9879\u76EE\u6C9F\u901A\u7BA1\u7406" aria-hidden="true">#</a> \u5982\u4F55\u505A\u597D\u9879\u76EE\u6C9F\u901A\u7BA1\u7406</h2><p><img src="'+n+'" alt="\u9879\u76EE\u6C9F\u901A\u7BA1\u7406"></p><h2 id="\u89C4\u5212\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u89C4\u5212\u8FC7\u7A0B\u7EC4</h2><h3 id="\u89C4\u5212\u6C9F\u901A\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u6C9F\u901A\u7BA1\u7406" aria-hidden="true">#</a> \u89C4\u5212\u6C9F\u901A\u7BA1\u7406</h3><p>\u57FA\u4E8E\u6BCF\u4E2A\u76F8\u5173\u65B9\u6216\u76F8\u5173\u65B9\u7FA4\u4F53\u7684\u4FE1\u606F\u9700\u6C42\u3001\u53EF\u7528\u7684\u7EC4\u7EC7\u8D44\u4EA7, \u4EE5\u53CA\u5177\u4F53\u9879\u76EE\u7684\u9700\u6C42, \u4E3A\u9879\u76EE\u6C9F\u901A\u6D3B\u52A8\u5236\u5B9A\u6070\u5F53\u7684\u65B9\u6CD5\u548C\u8BA1\u5212\u7684\u8FC7\u7A0B\u3002</p><p>\u4F5C\u7528\uFF1A\u6839\u636E\u76F8\u5173\u65B9\u9700\u6C42\u3001\u7EC4\u7EC7\u5B9E\u9645\u6761\u4EF6\u3001\u9879\u76EE\u7684\u60C5\u51B5, \u5236\u5B9A\u5408\u9002\u7684\u9879\u76EE\u6C9F\u901A\u8BA1\u5212, \u4F7F\u5F97\u9879\u76EE\u6C9F\u901A\u6548\u7387\u9AD8\u3001\u6548\u679C\u597D\u3002</p><h4 id="itto" tabindex="-1"><a class="header-anchor" href="#itto" aria-hidden="true">#</a> ITTO</h4><p>\u6C9F\u901A\u9700\u6C42\u5206\u6790 =&gt; \u5206\u6790\u6C9F\u901A\u9700\u6C42, \u786E\u5B9A\u9879\u76EE\u9879\u76EE\u76F8\u5173\u65B9\u7684\u4FE1\u606F\u9700\u6C42, \u5305\u62EC\u6240\u9700\u4FE1\u606F\u7684\u7C7B\u578B\u548C\u683C\u5F0F, \u4EE5\u53CA\u4FE1\u606F\u5BF9\u76F8\u5173\u65B9\u7684\u4EF7\u503C\u3002</p><p>\u6C9F\u901A\u6280\u672F\uFF1A\u4F1A\u8BAE\u3001\u7F51\u7AD9\u7B49\u5404\u79CD\u6280\u672F\u3002\u9700\u8981\u8003\u8651\u7D27\u8FEB\u6027\u3001\u53EF\u7528\u6027\u3001\u6613\u7528\u6027\u3001\u654F\u611F\u6027\u548C\u4FDD\u5BC6\u6027\u3002</p><p>\u6C9F\u901A\u6A21\u578B\uFF1A\u7F16\u7801\u3001\u4F20\u9012\u4FE1\u606F\u3001\u89E3\u7801\u3001\u786E\u8BA4\u5DF2\u6536\u5230\uFF08\u544A\u77E5\u6536\u6089\uFF09\u3001\u53CD\u9988 / \u54CD\u5E94\u3002</p><p>\u6C9F\u901A\u65B9\u6CD5\uFF1A</p><ul><li>\u4E92\u52A8\u6C9F\u901A\uFF1A\u5728\u4E24\u65B9\u6216\u591A\u65B9\u4E4B\u95F4\u8FDB\u884C\u7684\u5B9E\u65F6\u591A\u5411\u4FE1\u606F\u4EA4\u6362\u3002</li><li>\u63A8\u5F0F\u6C9F\u901A\uFF1A\u5411\u9700\u8981\u63A5\u6536\u4FE1\u606F\u7684\u7279\u5B9A\u63A5\u6536\u65B9\u53D1\u9001\u6216\u53D1\u5E03\u4FE1\u606F\u3002</li><li>\u62C9\u5F0F\u6C9F\u901A\uFF1A\u9002\u7528\u4E8E\u5927\u91CF\u590D\u6742\u4FE1\u606F\u6216\u5927\u91CF\u4FE1\u606F\u53D7\u4F17\u7684\u60C5\u51B5\u3002</li></ul><p>\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212\uFF1A\u63CF\u8FF0\u5C06\u5982\u4F55\u89C4\u5212, \u7ED3\u679C\u5316\u3001\u6267\u884C\u4E0E\u76D1\u7763\u9879\u76EE\u6C9F\u901A, \u4EE5\u4FBF\u63D0\u4F9B\u6C9F\u901A\u7684\u6709\u6548\u6027\u3002\u4FE1\u606F\u4F20\u9012\u6709\u95EE\u9898, \u4E00\u5F8B\u627E\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212</p><p><img src="'+c+'" alt="\u9879\u76EE\u6C9F\u901A\u7BA1\u7406-\u89C4\u5212\u6C9F\u901A\u7BA1\u7406"></p><h2 id="\u6267\u884C\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u6267\u884C\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u6267\u884C\u8FC7\u7A0B\u7EC4</h2><h3 id="\u7BA1\u7406\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u7BA1\u7406\u6C9F\u901A" aria-hidden="true">#</a> \u7BA1\u7406\u6C9F\u901A</h3><p>\u6267\u884C\u8FC7\u7A0B, \u5B9E\u9645\u505A\u6C9F\u901A\u7684\u8FC7\u7A0B, \u4FC3\u8FDB\u9879\u76EE\u56E2\u961F\u4E0E\u76F8\u5173\u65B9\u4E4B\u95F4\u7684\u6709\u6548\u4FE1\u606F\u6D41\u52A8\u3002</p><p>\u7BA1\u7406\u6C9F\u901A\u662F\u786E\u4FDD\u9879\u76EE\u4FE1\u606F\u53CA\u65F6\u4E14\u6070\u5F53\u5730\u6536\u96C6\u3001\u751F\u6210\u3001\u53D1\u5E03\u3001\u5B58\u50A8\u3001\u68C0\u7D22\u3001\u7BA1\u7406\u3001\u76D1\u7763\u548C\u6700\u7EC8\u5904\u7F6E\u7684\u8FC7\u7A0B\u3002\u4E3B\u8981\u4F5C\u7528\u662F, \u4FC3\u6210\u9879\u76EE\u56E2\u961F\u4E0E\u76F8\u5173\u65B9\u4E4B\u95F4\u7684\u6709\u6548\u4FE1\u606F\u6D41\u52A8</p><h4 id="itto-1" tabindex="-1"><a class="header-anchor" href="#itto-1" aria-hidden="true">#</a> ITTO</h4><p>\u9879\u76EE\u62A5\u544A\u53D1\u5E03\uFF1A\u6536\u96C6\u548C\u53D1\u5E03\u9879\u76EE\u4FE1\u606F\u7684\u884C\u4E3A\u3002\u867D\u7136\u5DE5\u4F5C\u7EE9\u6548\u62A5\u544A\u662F\u76D1\u63A7\u9879\u76EE\u5DE5\u4F5C\u8FC7\u7A0B\u7684\u8F93\u51FA, \u4F46\u662F\u672C\u8FC7\u7A0B\u4F1A\u7F16\u5236\u4E34\u65F6\u62A5\u544A\u3001\u9879\u76EE\u6F14\u793A\u3001\u535A\u5BA2, \u4EE5\u53CA\u5176\u4ED6\u7C7B\u578B\u7684\u4FE1\u606F\u3002</p><p>\u9879\u76EE\u6C9F\u901A\u8BB0\u5F55\uFF1A\u7EE9\u6548\u62A5\u544A\u3001\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u72B6\u6001\u3001\u8FDB\u5EA6\u8FDB\u5C55\u3001\u4EA7\u751F\u7684\u6210\u672C\u3001\u6F14\u793A, \u4EE5\u53CA\u76F8\u5173\u65B9\u9700\u8981\u7684\u5176\u4ED6\u4FE1\u606F\u3002</p><p><img src="'+h+'" alt="\u9879\u76EE\u6C9F\u901A\u7BA1\u7406-\u7BA1\u7406\u6C9F\u901A"></p><h2 id="\u76D1\u63A7\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u76D1\u63A7\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u76D1\u63A7\u8FC7\u7A0B\u7EC4</h2><h3 id="\u76D1\u7763\u6C9F\u901A" tabindex="-1"><a class="header-anchor" href="#\u76D1\u7763\u6C9F\u901A" aria-hidden="true">#</a> \u76D1\u7763\u6C9F\u901A</h3><p>\u76D1\u7763\u6C9F\u901A\u662F\u786E\u4FDD\u6EE1\u8DB3\u9879\u76EE\u53CA\u5176\u76F8\u5173\u65B9\u7684\u4FE1\u606F\u9700\u6C42\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4E3B\u8981\u4F5C\u7528\u662F, \u6309\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212\u548C\u76F8\u5173\u65B9\u53C2\u4E0E\u8BA1\u5212\u7684\u8981\u6C42\u4F18\u5316\u4FE1\u606F\u4F20\u9012\u6D41\u7A0B\u3002</p><p>\u9879\u76EE\u6C9F\u901A\u7684\u5F71\u54CD\u548C\u7ED3\u679C\u5E94\u8BE5\u63A5\u53D7\u8BA4\u771F\u7684\u8BC4\u4F30\u548C\u76D1\u7763, \u4EE5\u786E\u4FDD\u5728\u6B63\u786E\u7684\u65F6\u95F4, \u901A\u8FC7\u6B63\u786E\u7684\u6E20\u9053, \u5C06\u6B63\u786E\u7684\u5185\u5BB9\uFF08\u53D1\u9001\u65B9\u548C\u63A5\u6536\u65B9\u5BF9\u5176\u7406\u89E3\u4E00\u81F4\uFF09\u4F20\u9012\u7ED9\u6B63\u786E\u7684\u53D7\u4F17\u3002</p><h4 id="itto-2" tabindex="-1"><a class="header-anchor" href="#itto-2" aria-hidden="true">#</a> ITTO</h4><p><img src="'+o+'" alt="\u9879\u76EE\u6C9F\u901A\u7BA1\u7406-\u76D1\u7763\u6C9F\u901A"></p>',39),d=[s];function l(m,u){return e(),i("div",null,d)}var g=a(p,[["render",l],["__file","project-communication-management.html.vue"]]);export{g as default};
