import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as t,c as i,d as e}from"./app.a3d402e1.js";var r="/assets/project-cost-management.png",p="/assets/project-cost-management-0.png",l="/assets/project-cost-management-1.png",s="/assets/project-cost-management-2.png",h="/assets/project-cost-management-3.png",n="/assets/project-cost-management-4.png";const d={},c=e('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55\u9879\u76EE\u6210\u672C\u7BA1\u7406\u7AE0\u8282\u7684\u76F8\u5173\u77E5\u8BC6\u3001\u9891\u7E41\u8003\u70B9\u3002</p></div><h2 id="\u6838\u5FC3\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u6982\u5FF5" aria-hidden="true">#</a> \u6838\u5FC3\u6982\u5FF5</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u6210\u672C\u7BA1\u7406\u662F\u6307\u5C3D\u91CF\u8BA9\u9879\u76EE\u5B9E\u9645\u8FDB\u884C\u4E2D\u7684\u6210\u672C\u63A7\u5236\u5728\u9884\u7B97\u8303\u56F4\u4E4B\u5185, \u786E\u4FDD\u5728\u9884\u7B97\u4E4B\u5185\u5B8C\u6210\u9879\u76EE, \u4E3B\u8981\u5305\u62EC\u89C4\u5212\u6210\u672C\u7BA1\u7406\u3001\u4F30\u7B97\u6210\u672C\u3001\u5236\u5B9A\u9884\u7B97\u548C\u63A7\u5236\u6210\u672C\u56DB\u4E2A\u8FC7\u7A0B\u3002</p></div><p>\u9879\u76EE\u6210\u672C\u7BA1\u7406\u91CD\u70B9\u5173\u6CE8\u5B8C\u6210\u9879\u76EE\u6D3B\u52A8\u6240\u9700\u8D44\u6E90\u7684\u6210\u672C, \u4F46\u540C\u65F6\u4E5F\u5E94\u8003\u8651\u9879\u76EE\u51B3\u7B56\u5BF9\u9879\u76EE\u7684\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u7684\u4F7F\u7528\u6210\u672C\u3001\u7EF4\u62A4\u6210\u672C\u548C\u652F\u6301\u6210\u672C\u7684\u5F71\u54CD\u3002</p><p>\u9879\u76EE\u6210\u672C\u7BA1\u7406\u5305\u62EC\u4E3A\u4F7F\u9879\u76EE\u5728\u6279\u51C6\u7684\u9884\u7B97\u5185\u5B8C\u6210\u800C\u5BF9\u6210\u672C\u8FDB\u884C\u89C4\u5212\u3001\u4F30\u7B97\u3001\u9884\u7B97\u3001\u878D\u8D44\u3001\u7B79\u8D44\u3001\u7BA1\u7406\u548C\u63A7\u5236\u7684\u5404\u4E2A\u8FC7\u7A0B\u3002</p><p>\u9879\u76EE\u6210\u672C\u7BA1\u7406\u91CD\u70B9\u5173\u6CE8\u5B8C\u6210\u9879\u76EE\u6D3B\u52A8\u6240\u9700\u8D44\u6E90\u7684\u6210\u672C, \u4F46\u540C\u65F6\u4E5F\u5E94\u8003\u8651\u9879\u76EE\u51B3\u7B56\u5BF9\u9879\u76EE\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u7684\u4F7F\u7528\u6210\u672C\u3001\u7EF4\u62A4\u6210\u672C\u548C\u652F\u6301\u6210\u672C\u7684\u5F71\u54CD\u3002</p><h3 id="\u6210\u672C\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u6210\u672C\u7C7B\u578B" aria-hidden="true">#</a> \u6210\u672C\u7C7B\u578B</h3><ul><li>\u76F4\u63A5\u6210\u672C =&gt; \u76F4\u63A5\u53EF\u4EE5\u5F52\u5C5E\u4E8E\u9879\u76EE\u5DE5\u4F5C\u7684\u6210\u672C</li><li>\u95F4\u63A5\u6210\u672C =&gt; \u6765\u81EA\u4E00\u822C\u7BA1\u7406\u8D39\u7528\u79D1\u76EE\u3001\u6216\u591A\u4E2A\u9879\u76EE\u5206\u644A</li><li>\u56FA\u5B9A\u6210\u672C =&gt; \u4E0D\u968F\u751F\u4EA7\u91CF\u3001\u5DE5\u4F5C\u91CF\u6216\u65F6\u95F4\u7684\u53D8\u5316\u800C\u53D8\u5316\u7684\u4F1A\u91CD\u590D\u6210\u672C</li><li>\u53EF\u53D8\u6210\u672C =&gt; \u968F\u7740\u751F\u4EA7\u91CF\u3001\u5DE5\u4F5C\u91CF\u6216\u65F6\u95F4\u800C\u53D8\u7684\u6210\u672C, \u53C8\u79F0\u53D8\u52A8\u6210\u672C</li><li>\u5168\u751F\u547D\u5468\u671F\u6210\u672C =&gt; \u5728\u4EA7\u54C1\u7ECF\u6D4E\u6709\u6548\u4F7F\u7528\u671F\u95F4\u6240\u53D1\u751F\u7684\u4E0E\u8BE5\u4EA7\u54C1\u6709\u5173\u7684\u6240\u6709\u6210\u672C</li><li>\u6C89\u6CA1\u6210\u672C =&gt; \u4EFB\u4F55\u5DF2\u53D1\u751F\u7684\u6210\u672C, \u4E0E\u662F\u5426\u5408\u7406\u65E0\u5173\u3002\u5728\u51B3\u5B9A\u662F\u5426\u7EE7\u7EED\u67D0\u4E2A\u51FA\u4E86\u95EE\u9898\u7684\u9879\u76EE\u65F6, \u4E0D\u5E94\u8BE5\u8003\u8651\u6C89\u6CA1\u6210\u672C\u3002</li><li>\u673A\u4F1A\u6210\u672C =&gt; \u56E0\u4E3A\u9009\u62E9\u4E00\u4E2A\u9879\u76EE\u800C\u653E\u5F03\u53E6\u4E00\u4E2A\u9879\u76EE, \u53E6\u4E00\u9879\u76EE\u53EF\u80FD\u5E26\u6765\u7684\u5229\u76CA</li></ul><h2 id="\u601D\u7EF4\u5BFC\u56FE" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u5BFC\u56FE" aria-hidden="true">#</a> \u601D\u7EF4\u5BFC\u56FE</h2><p><img src="'+r+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406"></p><h2 id="\u9879\u76EE\u6210\u672C\u7BA1\u7406\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u6210\u672C\u7BA1\u7406\u6D41\u7A0B" aria-hidden="true">#</a> \u9879\u76EE\u6210\u672C\u7BA1\u7406\u6D41\u7A0B</h2><p>\u9879\u76EE\u6210\u672C\u7BA1\u7406\u5176\u5B9E\u5C31\u662F\u9879\u76EE\u201C\u8282\u6D41\u201D\u7684\u8FC7\u7A0B, \u4E00\u822C\u662F\u5BF9\u9879\u76EE\u7684\u8D44\u6E90\u548C\u8D39\u7528\u8FDB\u884C\u4F30\u7B97\u3001\u63A7\u5236\u548C\u7BA1\u7406.\u4E24\u4E2A\u65B9\u9762\u9700\u8981\u6CE8\u610F\uFF1A\u4E00\u662F\u5728\u9879\u76EE\u8FD0\u884C\u524D\u8981\u505A\u597D\u9884\u7B97\u8BA1\u5212\uFF1B\u4E8C\u662F\u5B9E\u9645\u82B1\u8D39\u8981\u6709\u771F\u5B9E\u3001\u5B9E\u65F6\u7684\u6570\u636E\u53CD\u9988\u3002\u9879\u76EE\u7BA1\u7406\u4EBA\u5458\u53EA\u6709\u5BF9\u8FD9\u4E24\u79CD\u6570\u636E\u8FDB\u884C\u5206\u6790\u7BA1\u7406, \u9879\u76EE\u6210\u672C\u7BA1\u7406\u624D\u4F1A\u66F4\u52A0\u79D1\u5B66\u548C\u5408\u7406, \u4F46\u6709\u4E00\u4E2A\u8BEF\u533A\u5927\u5BB6\u4E0D\u8981\u8E29\u5751, \u9879\u76EE\u6210\u672C\u7BA1\u7406 \u2260 \u8D8A\u7701\u94B1\u8D8A\u597D\u3002</p><p><img src="'+p+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406"></p><h2 id="\u89C4\u5212\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u89C4\u5212\u8FC7\u7A0B\u7EC4</h2><p>\u6210\u672C\u7BA1\u7406\u8BA1\u5212\u65E0\u6210\u672C, \u53EA\u662F\u4E00\u4E2A\u63CF\u8FF0\u5982\u4F55\u7BA1\u7406\u6210\u672C\u7684\u6307\u5357\u3002</p><h3 id="\u89C4\u5212\u6210\u672C\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u6210\u672C\u7BA1\u7406" aria-hidden="true">#</a> \u89C4\u5212\u6210\u672C\u7BA1\u7406</h3><h4 id="itto" tabindex="-1"><a class="header-anchor" href="#itto" aria-hidden="true">#</a> ITTO</h4><p>\u6210\u672C\u7BA1\u7406\u8BA1\u5212\uFF1A\u63CF\u8FF0\u5C06\u5982\u4F55\u89C4\u5212\u3001\u5B89\u6392\u548C\u63A7\u5236\u9879\u76EE\u6210\u672C\u3002</p><p>\u6210\u672C\u7BA1\u7406\u8BA1\u5212\u5305\u542B\u5185\u5BB9\uFF1A</p><p>\uFF081\uFF09\u8BA1\u91CF\u5355\u4F4D \uFF082\uFF09\u51C6\u786E\u5EA6 \uFF083\uFF09\u7CBE\u786E\u5EA6 \uFF084\uFF09\u7EC4\u7EC7\u7A0B\u5E8F\u94FE\u63A5 \uFF085\uFF09\u7EC4\u7EC7\u7A0B\u5E8F\u94FE\u63A5 \uFF086\uFF09\u63A7\u5236\u4E34\u754C\u503C \uFF087\uFF09\u7EE9\u6548\u6D4B\u91CF\u89C4\u5212</p><p><img src="'+l+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406-\u89C4\u5212\u6210\u672C\u7BA1\u7406"></p><h3 id="\u4F30\u7B97\u6210\u672C" tabindex="-1"><a class="header-anchor" href="#\u4F30\u7B97\u6210\u672C" aria-hidden="true">#</a> \u4F30\u7B97\u6210\u672C</h3><p>\u4F30\u7B97\u6210\u672C\u662F\u5BF9\u5B8C\u6210\u9879\u76EE\u5DE5\u4F5C\u6240\u9700\u8D44\u6E90\u6210\u672C\u8FDB\u884C\u8FD1\u4F3C\u4F30\u7B97\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4F5C\u7528\uFF1A\u786E\u5B9A\u9879\u76EE\u6240\u9700\u7684\u8D44\u91D1\u3002</p><p>\u901A\u5E38\u7528\u67D0\u79CD\u8D27\u5E01\u5355\u4F4D\u8FDB\u884C\u6210\u672C\u4F30\u7B97, \u4F46\u6709\u65F6\u4E5F\u53EF\u91C7\u7528\u5176\u4ED6\u8BA1\u91CF\u5355\u4F4D, \u5982\u4EBA\u65F6\u6570\u6216\u4EBA\u5929\u6570, \u4EE5\u6D88\u9664\u901A\u8D27\u81A8\u80C0\u7684\u5F71\u54CD, \u4FBF\u4E8E\u6210\u672C\u6BD4\u8F83\u3002</p><p>\u8303\u56F4\u57FA\u51C6\uFF1A\u9879\u76EE\u8303\u56F4\u8BF4\u660E\u4E66\u3001WBS\u3001WBS \u8BCD\u5178</p><p>\u4F30\u7B97\u6210\u672C\u8FC7\u7A0B\u5F97\u5230\u7684\u6210\u672C\u4F30\u7B97, \u901A\u5E38\u5305\u62EC\u4EE5\u4E0B\u5185\u5BB9\uFF1A</p><ol><li>\u9879\u76EE\u6240\u4F7F\u7528\u7684\u5168\u90E8\u8D44\u6E90\u7684\u6210\u672C</li><li>\u76F4\u63A5\u6210\u672C\u548C\u95F4\u63A5\u6210\u672C</li><li>\u5E94\u6025\u50A8\u5907\u548C\u7BA1\u7406\u50A8\u5907</li></ol><h4 id="itto-1" tabindex="-1"><a class="header-anchor" href="#itto-1" aria-hidden="true">#</a> ITTO</h4><p><img src="'+s+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406-\u4F30\u7B97\u6210\u672C"></p><h3 id="\u5236\u5B9A\u9884\u7B97" tabindex="-1"><a class="header-anchor" href="#\u5236\u5B9A\u9884\u7B97" aria-hidden="true">#</a> \u5236\u5B9A\u9884\u7B97</h3><p>\u5236\u5B9A\u9884\u7B97\u662F\u6C47\u603B\u6240\u6709\u5355\u4E2A\u6D3B\u52A8\u6216\u5DE5\u4F5C\u5305\u7684\u4F30\u7B97\u6210\u672C, \u5EFA\u7ACB\u4E00\u4E2A\u7ECF\u6279\u51C6\u7684\u6210\u672C\u57FA\u51C6\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4E3B\u8981\u4F5C\u7528\u662F, \u786E\u5B9A\u53EF\u636E\u4EE5\u76D1\u7763\u548C\u63A7\u5236\u9879\u76EE\u7EE9\u6548\u7684\u6210\u672C\u57FA\u51C6\u3002</p><p>\u9879\u76EE\u8FDB\u5EA6\u8BA1\u5212\uFF1A\u9879\u76EE\u8FDB\u5EA6\u8BA1\u5212\u5305\u62EC\u9879\u76EE\u6D3B\u52A8\u3001\u91CC\u7A0B\u7891\u3001\u5DE5\u4F5C\u5305\u548C\u63A7\u5236\u8D26\u6237\u7684\u8BA1\u5212\u5F00\u59CB\u548C\u5B8C\u6210\u65E5\u671F\u3002\u53EF\u6839\u636E\u8FD9\u4E9B\u4FE1\u606F, \u628A\u8BA1\u5212\u6210\u672C\u548C\u5B9E\u9645\u6210\u672C\u6C47\u603B\u5230\u76F8\u5E94\u7684\u65E5\u5386\u65F6\u6BB5\u4E2D\u3002</p><p>\u6210\u672C\u57FA\u51C6\uFF1A\u7ECF\u8FC7\u6279\u51C6\u7684\u3001\u6309\u65F6\u95F4\u6BB5\u5206\u914D\u7684\u9879\u76EE\u9884\u7B97, \u4E0D\u5305\u62EC\u4EFB\u4F55\u7BA1\u7406\u50A8\u5907, \u53EA\u6709\u901A\u8FC7\u6B63\u5F0F\u7684\u53D8\u66F4\u63A7\u5236\u7A0B\u5E8F\u624D\u80FD\u53D8\u66F4, \u7528\u4F5C\u4E0E\u5B9E\u9645\u7ED3\u679C\u8FDB\u884C\u6BD4\u8F83\u7684\u4F9D\u636E\u3002\u6210\u672C\u57FA\u51C6\u662F\u4E0D\u540C\u8FDB\u5EA6\u6D3B\u52A8\u7ECF\u6279\u51C6\u7684\u9884\u7B97\u7684\u603B\u548C\u3002</p><p>\u6210\u672C\u57FA\u51C6\u5305\u542B\u5E94\u6025\u50A8\u5907, \u4F46\u4E0D\u5305\u62EC\u7BA1\u7406\u50A8\u5907\u3002\u60F3\u8981\u4F7F\u7528\u7BA1\u7406\u50A8\u5907\u9700\u8981\u8D70\u53D8\u66F4\u6D41\u7A0B, \u9879\u76EE\u7ECF\u7406\u4E0D\u80FD\u76F4\u63A5\u4F7F\u7528\u3002</p><p>\u6210\u672C\u57FA\u51C6+\u7BA1\u7406\u57FA\u51C6=\u9879\u76EE\u9884\u7B97</p><h4 id="itto-2" tabindex="-1"><a class="header-anchor" href="#itto-2" aria-hidden="true">#</a> ITTO</h4><p><img src="'+h+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406-\u5236\u5B9A\u9884\u7B97"></p><h2 id="\u63A7\u5236\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u63A7\u5236\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u63A7\u5236\u8FC7\u7A0B\u7EC4</h2><h3 id="\u63A7\u5236\u6210\u672C" tabindex="-1"><a class="header-anchor" href="#\u63A7\u5236\u6210\u672C" aria-hidden="true">#</a> \u63A7\u5236\u6210\u672C</h3><p>\u63A7\u5236\u6210\u672C\u662F\u76D1\u7763\u9879\u76EE\u72B6\u6001, \u4EE5\u66F4\u65B0\u9879\u76EE\u6210\u672C\u548C\u7BA1\u7406\u6210\u672C\u57FA\u51C6\u53D8\u66F4\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4E3B\u8981\u4F5C\u7528\u662F, \u5728\u6574\u4E2A\u9879\u76EE\u671F\u95F4\u4FDD\u6301\u5BF9\u6210\u672C\u57FA\u51C6\u7684\u7EF4\u62A4\u3002</p><h4 id="itto-3" tabindex="-1"><a class="header-anchor" href="#itto-3" aria-hidden="true">#</a> ITTO</h4><ul><li><p>\u6323\u503C\u5206\u6790\uFF1ABAC\u3001PV\u3001EV\u3001AC</p></li><li><p>\u504F\u5DEE\u5206\u6790\uFF1ASV\u3001CV\u3001SPI\u3001CPI</p></li><li><p>\u8D8B\u52BF\u5206\u6790\uFF1AEAC\u3001ETC</p></li><li><p>SV = EV - PV &lt; 0\uFF1BSPI = EV / PC &lt; 1 =&gt; \u8FDB\u5EA6\u843D\u540E =&gt; \u9700\u8981\u91C7\u53D6\u8FDB\u5EA6\u538B\u7F29\u3001\u8D76\u5DE5\u3001\u5FEB\u901F\u8DDF\u8FDB\u65B9\u6CD5</p></li><li><p>SV = EV - PV = 0\uFF1BSPI = EV / PC = 1 =&gt; \u4E0E\u8BA1\u5212\u4E00\u81F4</p></li><li><p>SV = EV - PV &gt; 0\uFF1BSPI = EV / PC &gt; 1 =&gt; \u8FDB\u5EA6\u63D0\u524D</p></li><li><p>CV = EV - AC &lt; 0\uFF1BCPI = EV / AC &lt; 1 =&gt; \u6210\u672C\u8D85\u652F =&gt; \u9700\u8981\u91C7\u53D6\u8D44\u6E90\u5E73\u6ED1\u3001\u8D44\u6E90\u5E73\u8861</p></li><li><p>CV = EV - AC = 0\uFF1BCPI = EV / AC = 1 =&gt; \u4E0E\u8BA1\u5212\u4E00\u81F4</p></li><li><p>CV = EV - AC &gt; 0\uFF1BCPI = EV / AC &gt; 1 =&gt; \u6210\u672C\u8282\u7EA6</p></li><li><p>TCPI = (BAC - EV) / (BAC - AC) &gt; 1 =&gt; \u5F88\u96BE\u5B8C\u6210</p></li><li><p>TCPI = (BAC - EV) / (BAC - AC) = 1 =&gt; \u6B63\u597D\u5B8C\u6210</p></li><li><p>TCPI = (BAC - EV) / (BAC - AC) &lt; 1 =&gt; \u5F88\u5BB9\u6613\u5B8C\u6210</p></li><li><p>SPI \u4EC5\u53CD\u6620\u603B\u5DE5\u4F5C\u91CF, \u8FD8\u9700\u8981\u5BF9\u5173\u952E\u8DEF\u5F84\u5355\u72EC\u98CE\u9669\u3002</p></li><li><p>EV = BAC\uFF1B\u5F53\u6323\u503C\u7B49\u4E8E\u9884\u7B97\u65F6, \u8BF4\u660E\u9879\u76EE\u7684\u603B\u8BA1\u5212\u4EF7\u503C\u5DF2\u88AB\u5B8C\u5168\u6323\u56DE, \u9879\u76EE\u5DF2\u7ECF\u5B8C\u5DE5\u3002</p></li></ul><p><img src="'+n+'" alt="\u9879\u76EE\u6210\u672C\u7BA1\u7406-\u63A7\u5236\u6210\u672C"></p>',43),o=[c];function g(m,C){return t(),i("div",null,o)}var u=a(d,[["render",g],["__file","project-cost-management.html.vue"]]);export{u as default};
