import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as e,d as r}from"./app.a3d402e1.js";var t="/assets/project-integration-management.png",h="/assets/project-integration-management-1.png",l="/assets/project-integration-management-2.png",n="/assets/project-integration-management-3.png",d="/assets/project-integration-management-4.png",p="/assets/project-integration-management-5.png",s="/assets/project-integration-management-6.png",o="/assets/project-integration-management-7.png";const c={},u=r('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55\u9879\u76EE\u6574\u5408\u7BA1\u7406\u7AE0\u8282\u7684\u76F8\u5173\u77E5\u8BC6\u3001\u9891\u7E41\u8003\u70B9\u3002</p></div><h2 id="\u6838\u5FC3\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u6982\u5FF5" aria-hidden="true">#</a> \u6838\u5FC3\u6982\u5FF5</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u6574\u4F53\u7BA1\u7406\u662F\u6307\u8BC6\u522B\u3001\u5B9A\u4E49\u3001\u7ED3\u5408\u3001\u7EDF\u4E00\u4E0E\u534F\u8C03\u9879\u76EE\u7BA1\u7406\u8FC7\u7A0B\u7EC4\u4E2D\u7684\u5404\u4E2A\u8FC7\u7A0B\u4EE5\u53CA\u9879\u76EE\u7BA1\u7406\u6D3B\u52A8, \u7528\u4E00\u53E5\u8BDD\u6765\u603B\u7ED3\u9879\u76EE\u6574\u5408\u7BA1\u7406, \u5176\u4F5C\u7528\u72B9\u5982\u9879\u94FE\u4E2D\u7684\u90A3\u6839\u7EBF, \u5C06\u9879\u76EE\u7BA1\u7406\u8FC7\u7A0B\u7EC4\u4E2D\u9700\u8981\u7684\u5404\u4E2A\u8FC7\u7A0B\u8FDB\u884C\u6709\u6548\u7EFC\u5408\u3002</p><p>\u6574\u7406\u7BA1\u7406\u7684\u4E3B\u8981\u5185\u5BB9\u662F\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B\u3001\u5236\u5B9A\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u3001\u6307\u5BFC\u548C\u7BA1\u7406\u9879\u76EE\u5DE5\u4F5C\u3001\u76D1\u7763\u9879\u76EE\u5DE5\u4F5C\u7B49\u3002</p></div><ul><li>\u5728\u9879\u76EE\u7BA1\u7406\u4E2D, \u6574\u5408\u517C\u5177\u7EDF\u4E00\u3001\u5408\u5E76\u3001\u6C9F\u901A\u548C\u5EFA\u7ACB\u8054\u7CFB\u7684\u6027\u8D28, \u8FD9\u4E9B\u884C\u52A8\u5E94\u8BE5\u8D2F\u7A7F\u9879\u76EE\u59CB\u7EC8</li><li>\u9879\u76EE\u6574\u5408\u7BA1\u7406\u7531\u9879\u76EE\u7ECF\u7406\u8D1F\u8D23, \u5E76\u4E14\u6574\u5408\u7BA1\u7406\u7684\u8D23\u4EFB\u4E0D\u80FD\u88AB\u6388\u6743\u6216\u8005\u8F6C\u79FB</li><li>\u9879\u76EE\u7ECF\u7406\u5FC5\u987B\u5BF9\u6574\u4E2A\u9879\u76EE\u627F\u62C5\u6700\u7EC8\u8D23\u4EFB</li><li>\u9879\u76EE\u8D8A\u590D\u6742, \u76F8\u5173\u65B9\u7684\u671F\u671B\u8D8A\u591A\u6837\u5316,\u5C31\u9700\u8981\u8D8A\u5168\u9762\u7684\u6574\u5408\u65B9\u6CD5</li></ul><h2 id="\u53D1\u5C55\u8D8B\u52BF\u548C\u65B0\u5174\u5B9E\u8DF5" tabindex="-1"><a class="header-anchor" href="#\u53D1\u5C55\u8D8B\u52BF\u548C\u65B0\u5174\u5B9E\u8DF5" aria-hidden="true">#</a> \u53D1\u5C55\u8D8B\u52BF\u548C\u65B0\u5174\u5B9E\u8DF5</h2><ul><li>\u9879\u76EE\u6574\u5408\u7BA1\u7406\u77E5\u8BC6\u9886\u57DF\u8981\u6C42\u6574\u5408\u6240\u6709\u5176\u4ED6\u77E5\u8BC6\u9886\u57DF\u7684\u6210\u679C</li><li>\u4E0E\u6574\u5408\u7BA1\u7406\u8FC7\u7A0B\u76F8\u5173\u7684\u53D1\u5C55\u8D8B\u52BF\u5305\u62EC <ul><li>\u4F7F\u7528\u81EA\u52A8\u5316\u5DE5\u5177</li><li>\u4F7F\u7528\u53EF\u89C6\u5316\u7BA1\u7406\u5DE5\u5177</li><li>\u9879\u76EE\u77E5\u8BC6\u7BA1\u7406</li><li>\u589E\u52A0\u9879\u76EE\u7ECF\u7406\u7684\u804C\u8D23</li><li>\u6DF7\u5408\u578B\u65B9\u6CD5</li></ul></li></ul><h2 id="\u8FED\u4EE3\u548C\u654F\u6377" tabindex="-1"><a class="header-anchor" href="#\u8FED\u4EE3\u548C\u654F\u6377" aria-hidden="true">#</a> \u8FED\u4EE3\u548C\u654F\u6377</h2><ul><li>\u56E2\u961F\u6210\u5458\u4EE5\u9886\u57DF\u4E13\u5BB6\u7684\u8EAB\u4EFD\u53C2\u4E0E\u6574\u5408\u7BA1\u7406 <ul><li>\u56E2\u961F\u6210\u5458\u81EA\u884C\u5236\u5B9A\u8BA1\u5212</li><li>\u56E2\u961F\u6210\u5458\u81EA\u884C\u51B3\u5B9A\u5404\u4E2A\u7EC4\u4EF6\u7684\u6574\u5408\u65B9\u5F0F</li></ul></li></ul><h2 id="\u5546\u4E1A\u8BBA\u8BC1" tabindex="-1"><a class="header-anchor" href="#\u5546\u4E1A\u8BBA\u8BC1" aria-hidden="true">#</a> \u5546\u4E1A\u8BBA\u8BC1</h2><p>\u5546\u4E1A\u8BBA\u8BC1\u5185\u5305\u62EC\u5BF9\u4E8E\u9879\u76EE\u76EE\u6807\u3001\u65B9\u6848\u6548\u76CA\u3001\u76F8\u5BF9\u91CD\u5927\u4E8B\u4EF6\u7684\u7B80\u8981\u6982\u53D9, \u91CC\u7A0B\u7891\u6307\u67D0\u4E2A\u65F6\u95F4\u8282\u70B9, \u4E0E\u4E4B\u5207\u5408\u3002</p><h2 id="\u9879\u76EE\u7BA1\u7406\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u7BA1\u7406\u6D41\u7A0B" aria-hidden="true">#</a> \u9879\u76EE\u7BA1\u7406\u6D41\u7A0B</h2><p>\u521B\u5EFA\u9879\u76EE\u7AE0\u7A0B =&gt; \u6536\u96C6\u9700\u6C42 =&gt; \u5B9A\u4E49\u8303\u56F4 =&gt; \u521B\u5EFA WBS =&gt; \u5B9A\u4E49\u6D3B\u52A8 =&gt; \u6392\u5217\u6D3B\u52A8\u987A\u5E8F =&gt; \u4F30\u7B97\u6D3B\u52A8\u6301\u7EED\u65F6\u95F4 =&gt; \u5236\u5B9A\u8FDB\u5EA6\u8BA1\u5212 =&gt; \u4F30\u7B97\u6210\u672C =&gt; \u5236\u5B9A\u9884\u7B97</p><h2 id="\u601D\u7EF4\u5BFC\u56FE" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u5BFC\u56FE" aria-hidden="true">#</a> \u601D\u7EF4\u5BFC\u56FE</h2><p><img src="'+t+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406"></p><h2 id="\u542F\u52A8\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u542F\u52A8\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u542F\u52A8\u8FC7\u7A0B\u7EC4</h2><h3 id="\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B" aria-hidden="true">#</a> \u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B</h3><p>\u7F16\u5199\u4E00\u4EFD\u6B63\u5F0F\u6279\u51C6\u9879\u76EE\u5E76\u6388\u6743\u9879\u76EE\u7ECF\u7406\u5728\u9879\u76EE\u6D3B\u52A8\u4E2D\u4F7F\u7528\u7EC4\u7EC7\u8D44\u6E90\u7684\u6587\u4EF6\u8FC7\u7A0B\u3002</p><p>\u9879\u76EE\u7531\u9879\u76EE\u4EE5\u5916\u7684\u5B9E\u4F53\u6765\u542F\u52A8,\u5982\u53D1\u8D77\u4EBA\u3001\u9879\u76EE\u96C6\u6216\u9879\u76EE\u7BA1\u7406\u529E\u516C\u5BA4\u3002</p><p>\u5C3D\u65E9\u786E\u8BA4\u5E76\u4EFB\u547D\u9879\u76EE\u7ECF\u7406,\u9879\u76EE\u7ECF\u7406\u5E94\u8BE5\u53C2\u4E0E\u9879\u76EE\u7AE0\u7A0B\u7684\u5236\u5B9A,\u4EE5\u4FBF\u5BF9\u9879\u76EE\u9700\u6C42\u6709\u57FA\u672C\u7684\u4E86\u89E3\u3002</p><p>\u6700\u597D\u5728\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B\u65F6\u5C31\u4EFB\u547D,\u6700\u665A\u4E5F\u5FC5\u987B\u5728\u89C4\u5212\u5F00\u59CB\u4E4B\u524D\u3002</p><p>\u9879\u76EE\u7AE0\u7A0B\u662F\u7531\u9879\u76EE\u542F\u52A8\u8005\u6216\u53D1\u8D77\u4EBA\u53D1\u5E03\u7684, \u6B63\u5F0F\u6279\u51C6\u9879\u76EE\u6210\u7ACB, \u5E76\u6388\u6743\u9879\u76EE\u7ECF\u7406\u52A8\u7528\u7EC4\u7EC7\u8D44\u6E90\u5F00\u5C55\u9879\u76EE\u6D3B\u52A8\u7684\u6587\u4EF6\u3002\uFF08\u662F\u9879\u76EE\u7684\u201C\u5BAA\u6CD5\u201D, \u662F\u9879\u76EE\u7ECF\u7406\u7684\u201C\u5C1A\u65B9\u5B9D\u5251\u201D\uFF09</p><p>\u5185\u5BB9\uFF1A</p><ul><li>\u59D4\u6D3E\u7684\u9879\u76EE\u7ECF\u7406\u53CA\u5176\u6743\u8D23</li><li>\u9879\u76EE\u7684\u76EE\u7684\u3001\u76EE\u6807\u3001\u9879\u76EE\u7684\u6210\u529F\u6807\u51C6</li><li>\u9AD8\u5C42\u7EA7\u7684\u9700\u6C42\u3001\u9AD8\u5C42\u7EA7\u7684\u9879\u76EE\u63CF\u8FF0\u3001\u9AD8\u5C42\u7EA7\u7684\u6218\u7565\u548C\u8FD0\u8425\u5047\u8BBE\u6761\u4EF6\u548C\u5236\u7EA6\u56E0\u7D20</li><li>\u603B\u4F53\u91CC\u7A0B\u7891\u8FDB\u5EA6\u8BA1\u5212\u3001\u603B\u4F53\u9884\u7B97\u3001\u6574\u4F53\u9879\u76EE\u98CE\u9669</li></ul><h4 id="itto" tabindex="-1"><a class="header-anchor" href="#itto" aria-hidden="true">#</a> ITTO</h4><p><img src="'+h+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u5236\u5B9A\u9879\u76EE\u7AE0\u7A0B"></p><h2 id="\u8BA1\u5212\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u5212\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u8BA1\u5212\u8FC7\u7A0B\u7EC4</h2><h3 id="\u5236\u5B9A\u9879\u76EE\u7BA1\u7406\u8BA1\u5212" tabindex="-1"><a class="header-anchor" href="#\u5236\u5B9A\u9879\u76EE\u7BA1\u7406\u8BA1\u5212" aria-hidden="true">#</a> \u5236\u5B9A\u9879\u76EE\u7BA1\u7406\u8BA1\u5212</h3><p>\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u7531\u9879\u76EE\u7684\u4E3B\u8981\u76F8\u5173\u65B9\u8FDB\u884C\u6279\u51C6\u3002</p><p>\u6307\u5357\uFF08\u4E00\u822C\u60C5\u51B5\u4E0B\u4E0D\u5141\u8BB8\u88AB\u4FEE\u6539\uFF09\uFF1A</p><p>\u8303\u56F4\u7BA1\u7406\u8BA1\u5212\u3001\u9700\u6C42\u7BA1\u7406\u8BA1\u5212\u3001\u8FDB\u5EA6\u7BA1\u7406\u8BA1\u5212\u3001\u6210\u672C\u7BA1\u7406\u8BA1\u5212\u3001\u8D28\u91CF\u7BA1\u7406\u8BA1\u5212\u3001\u8D44\u6E90\u7BA1\u7406\u8BA1\u5212\u3001\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212\u3001\u98CE\u9669\u7BA1\u7406\u8BA1\u5212\u3001\u91C7\u8D2D\u7BA1\u7406\u8BA1\u5212\u3001\u76F8\u5173\u65B9\u53C2\u4E0E\u8BA1\u5212\u3001\u53D8\u66F4\u7BA1\u7406\u8BA1\u5212\u3001\u914D\u7F6E\u7BA1\u7406\u8BA1\u5212\u3002</p><p>\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u662F\u201C\u6587\u4EF6\u201D, \u4F46\u4E0D\u662F\u201C\u9879\u76EE\u6587\u4EF6\u201D\u3002</p><p>\u51C6\u5907\u548C\u534F\u8C03\u9879\u76EE\u8BA1\u5212\u6240\u6709\u7EC4\u6210\u90E8\u5206, \u5E76\u628A\u5B83\u4EEC\u6574\u5408\u4E3A\u4E00\u4EFD\u7EFC\u5408\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u7684\u8FC7\u7A0B\u3002</p><p>\u8BA1\u5212\u9700\u8981\u901A\u8FC7\u4E0D\u65AD\u66F4\u65B0\u6765\u6E10\u8FDB\u660E\u7EC6, \u5E76\u4E14\u8FD9\u4E9B\u66F4\u65B0\u9700\u8981\u5F97\u5230\u63A7\u5236\u548C\u6279\u51C6\u3002</p><p>\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u4E00\u65E6\u88AB\u786E\u5B9A\u4E3A\u57FA\u51C6, \u5C31\u53EA\u6709\u5728\u63D0\u51FA\u53D8\u66F4\u8BF7\u6C42\u5E76\u7ECF\u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7\u5236\u8FC7\u7A0B\u6279\u51C6\u540E, \u624D\u80FD\u53D8\u66F4\u3002</p><p>\u901A\u5E38\u5C06\u8303\u56F4\u3001\u8FDB\u5EA6\u548C\u6210\u672C\u57FA\u51C6\u5408\u5E76\u4E3A\u4E00\u4E2A\u7EE9\u6548\u6D4B\u91CF\u57FA\u51C6, \u4F5C\u4E3A\u9879\u76EE\u7684\u6574\u4F53\u57FA\u51C6, \u4EE5\u4FBF\u636E\u6B64\u6D4B\u91CF\u9879\u76EE\u7684\u6574\u4F53\u7EE9\u6548\u3002 \u5B50\u7BA1\u7406\u8BA1\u5212\u5305\u62EC\uFF08\u4F46\u4E0D\u9650\u4E8E\uFF09\uFF1A</p><p>\uFF081\uFF09\u8303\u56F4\u7BA1\u7406\u8BA1\u5212 \uFF082\uFF09\u9700\u6C42\u7BA1\u7406\u8BA1\u5212 \uFF083\uFF09\u8FDB\u5EA6\u7BA1\u7406\u8BA1\u5212 \uFF084\uFF09\u6210\u672C\u7BA1\u7406\u8BA1\u5212 \uFF085\uFF09\u8D28\u91CF\u7BA1\u7406\u8BA1\u5212 \uFF086\uFF09\u8D44\u6E90\u7BA1\u7406\u8BA1\u5212 \uFF087\uFF09\u6C9F\u901A\u7BA1\u7406\u8BA1\u5212 \uFF088\uFF09\u98CE\u9669\u7BA1\u7406\u8BA1\u5212 \uFF089\uFF09\u91C7\u8D2D\u7BA1\u7406\u8BA1\u5212 \uFF0810\uFF09\u76F8\u5173\u65B9\u53C2\u4E0E\u7406\u8BA1\u5212</p><p>\u9879\u76EE\u57FA\u51C6\u5305\u62EC\uFF1A</p><p>\uFF081\uFF09\u8FDB\u5EA6\u57FA\u51C6\uFF08\u505A\u591A\u5C11\uFF09 \uFF082\uFF09\u6210\u672C\u7EE9\u6548\u57FA\u51C6\uFF08\u82B1\u591A\u5C11\uFF09 \uFF083\uFF09\u8303\u56F4\u57FA\u51C6\uFF08\u505A\u4EC0\u4E48\uFF09</p><p>\u5176\u4ED6\u7EC4\u4EF6\u5305\u62EC\uFF1A \uFF081\uFF09\u53D8\u8DDF\u7BA1\u7406\u8BA1\u5212 \uFF082\uFF09\u914D\u7F6E\u7BA1\u7406\u8BA1\u5212 \uFF083\uFF09\u7EE9\u6548\u6D4B\u91CF\u57FA\u51C6 \uFF084\uFF09\u9879\u76EE\u5468\u671F\u63CF\u8FF0 \uFF085\uFF09\u5F00\u53D1\u65B9\u6CD5 \uFF086\uFF09\u7BA1\u7406\u5BA1\u67E5</p><h4 id="itto-1" tabindex="-1"><a class="header-anchor" href="#itto-1" aria-hidden="true">#</a> ITTO</h4><p><img src="'+l+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u5236\u5B9A\u9879\u76EE\u7BA1\u7406\u8BA1\u5212"></p><h2 id="\u6267\u884C\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u6267\u884C\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u6267\u884C\u8FC7\u7A0B\u7EC4</h2><h3 id="\u6307\u5BFC\u4E0E\u7BA1\u7406\u9879\u76EE\u5DE5\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u6307\u5BFC\u4E0E\u7BA1\u7406\u9879\u76EE\u5DE5\u4F5C" aria-hidden="true">#</a> \u6307\u5BFC\u4E0E\u7BA1\u7406\u9879\u76EE\u5DE5\u4F5C</h3><p>\u6267\u884C\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u4E2D\u6240\u786E\u5B9A\u7684\u5DE5\u4F5C, \u5E76\u5B9E\u65BD\u5DF2\u6279\u51C6\u53D8\u66F4\u7684\u8FC7\u7A0B\u3002</p><h4 id="itto-2" tabindex="-1"><a class="header-anchor" href="#itto-2" aria-hidden="true">#</a> ITTO</h4><p><img src="'+n+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u6307\u5BFC\u4E0E\u7BA1\u7406\u9879\u76EE\u5DE5\u4F5C\u7A0B"></p><h3 id="\u7BA1\u7406\u9879\u76EE\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u7BA1\u7406\u9879\u76EE\u77E5\u8BC6" aria-hidden="true">#</a> \u7BA1\u7406\u9879\u76EE\u77E5\u8BC6</h3><p>\u4F7F\u7528\u73B0\u6709\u77E5\u8BC6\u5E76\u751F\u6210\u65B0\u77E5\u8BC6, \u4EE5\u5B9E\u73B0\u9879\u76EE\u76EE\u6807, \u5E76\u4E14\u5E2E\u52A9\u7EC4\u7EC7\u5B66\u4E60\u7684\u8FC7\u7A0B\u3002\u652F\u6301\u7EC4\u7EC7\u8FD0\u8425\u548C\u672A\u6765\u7684\u9879\u76EE\u6216\u9636\u6BB5\u3002</p><ul><li>\u663E\u6027\u77E5\u8BC6\uFF1A\u6613\u4F7F\u7528\u6587\u5B57\u3001\u56FE\u7247\u548C\u6570\u5B57\u8FDB\u884C\u7F16\u64B0\u7684\u77E5\u8BC6</li><li>\u9690\u6027\u77E5\u8BC6\uFF1A\u4E2A\u4EBA\u77E5\u8BC6\u4EE5\u53CA\u96BE\u4EE5\u660E\u786E\u8868\u8FBE\u7684\u77E5\u8BC6</li></ul><h4 id="itto-3" tabindex="-1"><a class="header-anchor" href="#itto-3" aria-hidden="true">#</a> ITTO</h4><p><img src="'+d+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u7BA1\u7406\u9879\u76EE\u77E5\u8BC6"></p><h2 id="\u76D1\u63A7\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u76D1\u63A7\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u76D1\u63A7\u8FC7\u7A0B\u7EC4</h2><h3 id="\u76D1\u63A7\u9879\u76EE\u5DE5\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u76D1\u63A7\u9879\u76EE\u5DE5\u4F5C" aria-hidden="true">#</a> \u76D1\u63A7\u9879\u76EE\u5DE5\u4F5C</h3><p>\u8DDF\u8E2A\u3001\u5BA1\u67E5\u548C\u62A5\u544A\u6574\u4F53\u9879\u76EE\u8FDB\u5C55, \u5B9E\u73B0\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u4E2D\u786E\u5B9A\u7684\u7EE9\u6548\u76EE\u6807\u7684\u8FC7\u7A0B\u3002\u76D1\u7763\u662F\u8D2F\u7A7F\u4E8E\u6574\u4E2A\u9879\u76EE\u7684\u9879\u76EE\u7BA1\u7406\u6D3B\u52A8\u4E4B\u4E00\u3002</p><h4 id="itto-4" tabindex="-1"><a class="header-anchor" href="#itto-4" aria-hidden="true">#</a> ITTO</h4><p><img src="'+p+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u76D1\u63A7\u9879\u76EE\u5DE5\u4F5C"></p><h3 id="\u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7\u5236" aria-hidden="true">#</a> \u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7\u5236</h3><p>\u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7\u5236\u8FC7\u7A0B\u8D2F\u7A7F\u9879\u76EE\u59CB\u7EC8, \u9879\u76EE\u7ECF\u7406\u5BF9\u6B64\u627F\u62C5\u6700\u7EC8\u8D23\u4EFB\u3002</p><p>\u9700\u8981\u4E86\u89E3\u53D8\u66F4\u5BF9\u8FDB\u5EA6\u7684\u5F71\u54CD\u548C\u5BF9\u6210\u672C\u7B49\u7B49\u5404\u65B9\u9762\u7684\u5F71\u54CD\u3002</p><p>\u9879\u76EE\u6587\u4EF6\u66F4\u65B0\uFF1A\u4E0D\u7BA1\u53D8\u66F4\u6709\u6CA1\u6709\u88AB\u6279\u51C6, \u90FD\u9700\u8981\u66F4\u65B0\u53D8\u66F4\u65E5\u5FD7\u3002</p><p>\u53D8\u66F4\u6D41\u7A0B\uFF1A</p><ul><li>\u3010\u8BB0\u5F55\u3011=&gt; \u4E66\u9762\u8BB0\u5F55\u53D8\u66F4\u8BF7\u6C42\uFF1B\u9879\u76EE\u7ECF\u7406\u4E66\u9762\u8BB0\u5F55\uFF08\u4E66\u9762\u65E5\u5FD7\uFF09, \u6216\u8981\u6C42\u53D8\u66F4\u63D0\u51FA\u8005\u63D0\u4EA4\u4E66\u9762\u7684\u53D8\u66F4\u8BF7\u6C42</li><li>\u3010\u8BC4\u4F30\u3011=&gt; \u5145\u5206\u4E86\u89E3\u53D8\u66F4, \u8BC4\u4F30\u53D8\u66F4\u5E26\u6765\u7684\u5F71\u54CD\uFF1B\u4E0E\u76F8\u5173\u65B9\u6C9F\u901A\u8BC4\u4F30\u51FA\u7684\u5F71\u54CD</li><li>\u3010\u63D0\u4EA4\u3011=&gt; \u63D0\u4EA4\u8D23\u4EFB\u4EBA\u5BA1\u6279\uFF1B\u6CE8\u610F, \u8FD9\u91CC\u7684\u63D0\u4EA4\u6307\u201C\u9879\u76EE\u7ECF\u7406\u201D\u5C06\u53D8\u66F4\u8BF7\u6C42\u548C\u8BC4\u4F30\u7684\u7ED3\u679C\u63D0\u4EA4\u7ED9 CCB</li><li>\u3010\u66F4\u65B0\u3011=&gt; \u4E0D\u7BA1\u53D8\u901A\u8FC7\u8FD8\u662F\u4E0D\u901A\u8FC7, \u5FC5\u987B\u66F4\u65B0\u65E5\u5FD7\uFF1B\u5982\u679C\u53D8\u66F4\u901A\u8FC7, \u66F4\u65B0\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\uFF08\u6587\u4EF6\uFF09</li><li>\u3010\u901A\u77E5\u3011=&gt; \u5E94\u5C06\u53D8\u66F4\u7684\u7ED3\u679C\u901A\u77E5\u76F8\u5173\uFF08\u53D7\u5F71\u54CD\uFF09\u7684\u76F8\u5173\u65B9</li></ul><h4 id="\u53D8\u66F4\u8BF7\u6C42-4-\u79CD\u60C5\u51B5" tabindex="-1"><a class="header-anchor" href="#\u53D8\u66F4\u8BF7\u6C42-4-\u79CD\u60C5\u51B5" aria-hidden="true">#</a> \u53D8\u66F4\u8BF7\u6C42 4 \u79CD\u60C5\u51B5</h4><ol><li>\u9884\u9632\u63AA\u65BD: \u4E3A\u786E\u4FDD\u9879\u76EE\u5DE5\u4F5C\u7684\u672A\u6765\u7EE9\u6548\u7B26\u5408\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u800C\u8FDB\u884C\u7684\u6709\u76EE\u7684\u7684\u6D3B\u52A8\u3002\u8FD9\u91CC\u7684\u9884\u9632\u5F53\u7136\u662F\u6700\u597D\u7684\u65B9\u5F0F, \u6839\u636E\u9884\u9632\u63CF\u8FF0, \u53EF\u4EE5\u51CF\u5C11\u6216\u8005\u6CA1\u6709\u53D8\u66F4\u3002\u5BF9\u5B9E\u9645\u5DE5\u4F5C\u6765\u8BF4\u80AF\u5B9A\u662F\u6700\u597D\u7684\u65B9\u5F0F, \u53D8\u66F4\u5C11, \u9879\u76EE\u6210\u529F\u7684\u53EF\u80FD\u6027\u5927, \u5F53\u7136\u524D\u63D0\u662F\u9879\u76EE\u8303\u56F4\u5B9A\u4E49\u7684\u786E\u5B9E\u7B26\u5408\u5BA2\u89C2\u5B9E\u9645\u9700\u6C42\u3002</li><li>\u7EA0\u6B63\u63AA\u65BD: \u4E3A\u4F7F\u9879\u76EE\u5DE5\u4F5C\u7EE9\u6548\u91CD\u65B0\u4E0E\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u4E00\u81F4\u800C\u8FDB\u884C\u7684\u6709\u76EE\u7684\u7684\u6D3B\u52A8\u3002\u7EA0\u6B63\u8BF4\u660E\u6709\u504F\u5DEE\u4E86\u3002\u6709\u504F\u5DEE\u8981\u7EA0\u6B63\u662F-\u5927\u7C7B\u9898\u76EE\u9009\u62E9\u4F9D\u636E\u3002</li><li>\u7F3A\u9677\u8865\u6551: \u4E3A\u4FEE\u6B63\u4E0D\u4E00\u81F4\u7684\u4EA7\u54C1\u6216\u4EA7\u54C1\u7EC4\u4EF6\u800C\u8FDB\u884C\u7684\u6709\u76EE\u7684\u7684\u6D3B\u52A8\u3002\u5DF2\u7ECF\u51FA\u73B0\u8D28\u91CF\u95EE\u9898\u4E86\u3002\u8BF4\u660E\u51FA\u73B0\u4E86\u8D28\u91CF\u95EE\u9898\u4E86, \u4E5F\u53EF\u4EE5\u7406\u89E3\u4E3A\u504F\u5DEE\u8FC7\u5927, \u5DF2\u7ECF\u5728\u53EF\u4EA4\u4ED8\u6210\u679C\u7269\u4E2D\u8868\u73B0\u51FA\u6765\u4E86\u3002\u8DDF\u7EA0\u6B63\u63AA\u65BD\u5BF9\u7167\u6BD4\u8F83, \u8BF4\u660E\u7EA0\u6B63\u63AA\u65BD\u4E2D\u7684\u504F\u5DEE\u8FD8\u6BD4\u8F83\u5C0F, \u672A\u5728\u53EF\u4EA4\u4ED8\u6210\u679C\u7269\u4E2D\u53D8\u73B0\u51FA\u6765\u8FD9\u79CD\u504F\u5DEE\u3002</li><li>\u66F4\u65B0: \u5BF9\u6B63\u5F0F\u53D7\u63A7\u7684\u9879\u76EE\u6587\u4EF6\u6216\u8BA1\u5212\u7B49\u8FDB\u884C\u7684\u53D8\u66F4, \u4EE5\u53CD\u6620\u4FEE\u6539\u6216\u589E\u52A0\u7684\u610F\u89C1\u6216\u5185\u5BB9\u3002\u8BF4\u660E\u5DF2\u7ECF\u4E3A\u4E86\u4FEE\u6B63\u8FD9\u79CD\u5DE8\u5927\u504F\u5DEE, \u9700\u8981\u5BF9\u9879\u76EE\u7684\u8BA1\u5212\u548C\u57FA\u51C6\u90FD\u9700\u8981\u8C03\u6574\u4E86\u3002</li></ol><h4 id="itto-5" tabindex="-1"><a class="header-anchor" href="#itto-5" aria-hidden="true">#</a> ITTO</h4><p><img src="'+s+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u5B9E\u65BD\u6574\u4F53\u53D8\u66F4\u63A7"></p><h2 id="\u6536\u5C3E\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u6536\u5C3E\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u6536\u5C3E\u8FC7\u7A0B\u7EC4</h2><h3 id="\u7ED3\u675F\u9879\u76EE\u6216\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u7ED3\u675F\u9879\u76EE\u6216\u9636\u6BB5" aria-hidden="true">#</a> \u7ED3\u675F\u9879\u76EE\u6216\u9636\u6BB5</h3><p>\u5B58\u6863\u9879\u76EE\u6216\u9636\u6BB5\u4FE1\u606F, \u5B8C\u6210\u8BA1\u5212\u7684\u5DE5\u4F5C, \u91CA\u653E\u7EC4\u7EC7\u56E2\u961F\u8D44\u6E90\u4EE5\u5C55\u5F00\u65B0\u7684\u5DE5\u4F5C\u3002</p><p>\u9879\u76EE\u7EC8\u6B62\u6D41\u7A0B\uFF086+2\uFF09\uFF1A</p><ol><li>\u83B7\u5F97\u9879\u76EE\u53D1\u8D77\u4EBA\u548C\u76F8\u5173\u65B9\u7684\u6279\u51C6\uFF08\u9A8C\u6536\uFF09</li><li>\u79FB\u4EA4\u6700\u7EC8\u6210\u679C</li><li>\u603B\u7ED3\u7ECF\u9A8C\u6559\u8BAD, \u63D0\u4F9B\u672A\u6765\u9879\u76EE\u6216\u9636\u6BB5\u4F7F\u7528</li><li>\u66F4\u65B0\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7</li><li>\u6587\u4EF6\u5F52\u6863</li><li>\u91CA\u653E\u8D44\u6E90\u3001\u89E3\u6563\u9879\u76EE\u56E2\u961F</li><li>\u8861\u91CF\u5E76\u8BB0\u5F55\u5BA2\u6237\u6EE1\u610F\u5EA6</li><li>\u5E86\u529F\u4F1A</li></ol><p>\u9879\u76EE\u63D0\u524D\u7EC8\u6B62\u6D41\u7A0B\uFF1A</p><ol><li>\u5728\u6B63\u5F0F\u7684\u6536\u5C3E\u6587\u4EF6\u4E2D\u8BF4\u660E\u9879\u76EE\u7EC8\u6B62\u7684\u539F\u56E0</li><li>\u628A\u8BE5\u9879\u76EE\u7684\u5DF2\u5B8C\u6210\u548C\u672A\u5B8C\u6210\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u79FB\u4EA4\u4ED6\u4EBA</li><li>\u603B\u7ED3\u7ECF\u9A8C\u6559\u8BAD, \u63D0\u4F9B\u672A\u6765\u9879\u76EE\u6216\u9636\u6BB5\u4F7F\u7528</li><li>\u66F4\u65B0\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7</li><li>\u6587\u4EF6\u5F52\u6863</li><li>\u91CA\u653E\u8D44\u6E90\u3001\u89E3\u6563\u9879\u76EE\u56E2\u961F</li></ol><p>\u9879\u76EE\u5728\u5B8C\u5DE5\u524D\u63D0\u524D\u7EC8\u6B62, \u5219\u9700\u8981\u5728\u6B63\u5F0F\u7684\u6536\u5C3E\u6587\u4EF6\u4E2D\u8BF4\u660E\u9879\u76EE\u7EC8\u6B62\u7684\u539F\u56E0, \u5E76\u89C4\u5B9A\u6B63\u5F0F\u7A0B\u5E8F, \u628A\u8BE5\u9879\u76EE\u7684\u5DF2\u5B8C\u6210\u548C\u672A\u5B8C\u6210\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u79FB\u4EA4\u4ED6\u4EBA\u3002</p><h4 id="itto-6" tabindex="-1"><a class="header-anchor" href="#itto-6" aria-hidden="true">#</a> ITTO</h4><p><img src="'+o+'" alt="\u9879\u76EE\u6574\u5408\u7BA1\u7406-\u7ED3\u675F\u9879\u76EE\u6216\u9636\u6BB5"></p>',76),g=[u];function m(f,_){return i(),e("div",null,g)}var v=a(c,[["render",m],["__file","project-integration-management.html.vue"]]);export{v as default};
