import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as t,d as l}from"./app.a3d402e1.js";var e="/assets/project-quality-management.png",r="/assets/project-quality-management-0.png",p="/assets/project-quality-management-1.png",s="/assets/project-quality-management-2.png",n="/assets/project-quality-management-3.png";const c={},h=l('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u7AE0\u8282\u7684\u76F8\u5173\u77E5\u8BC6\u3001\u9891\u7E41\u8003\u70B9\u3002</p></div><h2 id="\u6838\u5FC3\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u6982\u5FF5" aria-hidden="true">#</a> \u6838\u5FC3\u6982\u5FF5</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u4E3B\u8981\u5305\u542B\u4E86\u89C4\u5212\u8D28\u91CF\u7BA1\u7406\u3001\u7BA1\u7406\u8D28\u91CF\u3001\u63A7\u5236\u8D28\u91CF\u4E09\u4E2A\u8FC7\u7A0B, \u76EE\u7684\u662F\u505A\u51FA\u5408\u683C\u7684\u8D28\u91CF\u3001\u5EFA\u7ACB\u4FE1\u5FC3(\u5BA2\u6237/\u56E2\u961F) \u3001\u6EE1\u8DB3\u7279\u5B9A\u7684\u9700\u6C42\u548C\u671F\u671B\u3001\u5BA1\u8BA1\u8D28\u91CF\u8981\u6C42\u548C\u8D28\u91CF\u63A7\u5236\u6D4B\u91CF\u7ED3\u679C\u548C\u6539\u8FDB\u751F\u4EA7\u8FC7\u7A0B\u3002</p></div><p>\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u9700\u8981\u517C\u987E\u9879\u76EE\u7BA1\u7406\u4E0E\u9879\u76EE\u53EF\u4EA4\u4ED8\u6210\u679C\u4E24\u4E2A\u65B9\u9762\u3002</p><ul><li>\u3010\u8D28\u91CF\u3011\u4F5C\u4E3A\u5B9E\u73B0\u7684\u6027\u80FD\u6216\u6210\u679C, \u662F\u201C\u4E00\u7CFB\u5217\u5185\u5728\u7279\u6027\u6EE1\u8DB3\u8981\u6C42\u7684\u7A0B\u5EA6\u201D\u3002</li><li>\u3010\u7B49\u7EA7\u3011\u4F5C\u4E3A\u8BBE\u8BA1\u610F\u56FE, \u662F\u5BF9\u7528\u9014\u76F8\u540C\u4F46\u6280\u672F\u7279\u6027\u4E0D\u540C\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u7EA7\u522B\u5206\u7C7B\u3002</li></ul><p>\u4E94\u79CD\u8D28\u91CF\u7BA1\u7406\u6C34\u5E73\uFF1A\u5BA2\u6237 &lt; QC &lt; QA &lt; QP &lt; \u6587\u5316\u3002</p><ul><li>\u3010\u9884\u9632\u3011=&gt; \u4FDD\u8BC1\u8FC7\u7A0B\u603B\u5171\u4E0D\u51FA\u73B0\u9519\u8BEF</li><li>\u3010\u68C0\u67E5\u3011=&gt; \u4FDD\u8BC1\u9519\u8BEF\u4E0D\u843D\u5230\u5BA2\u6237\u624B\u4E2D</li><li>\u3010\u5C5E\u6027\u62BD\u6837\u3011=&gt; \u7ED3\u679C\u4E3A\u5408\u683C\u6216\u4E0D\u5408\u683C</li><li>\u3010\u53D8\u91CF\u62BD\u6837\u3011=&gt; \u8868\u660E\u5408\u683C\u7684\u7A0B\u5EA6</li><li>\u3010\u516C\u5DEE\u3011=&gt; \u7ED3\u679C\u7684\u53EF\u63A5\u53D7\u8303\u56F4</li><li>\u3010\u63A7\u5236\u754C\u9650\u3011=&gt; \u7A33\u5B9A\u8FC7\u7A0B\u7684\u666E\u901A\u504F\u5DEE\u7684\u8FB9\u754C</li></ul><p>\u9879\u76EE\u7ECF\u7406\u53CA\u9879\u76EE\u7BA1\u7406\u56E2\u961F\u8D1F\u8D23\u6743\u8861, \u4EE5\u4FBF\u540C\u65F6\u8FBE\u5230\u6240\u8981\u6C42\u7684\u8D28\u91CF\u4E0E\u7B49\u7EA7\u6C34\u5E73\u3002</p><p>\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u7684\u6838\u5FC3\u6982\u5FF5 =&gt; \u6309\u6709\u6548\u6027\u9012\u589E\u7684\u4E94\u79CD\u8D28\u91CF\u7BA1\u7406\u6C34\u5E73</p><h2 id="\u601D\u7EF4\u5BFC\u56FE" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u5BFC\u56FE" aria-hidden="true">#</a> \u601D\u7EF4\u5BFC\u56FE</h2><p><img src="'+e+'" alt="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406"></p><h2 id="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u6D41\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u6D41\u7A0B" aria-hidden="true">#</a> \u9879\u76EE\u8D28\u91CF\u7BA1\u7406\u6D41\u7A0B</h2><p><img src="'+r+'" alt="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406"></p><h2 id="\u89C4\u5212\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u89C4\u5212\u8FC7\u7A0B\u7EC4</h2><h3 id="\u89C4\u5212\u8D28\u91CF\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8D28\u91CF\u7BA1\u7406" aria-hidden="true">#</a> \u89C4\u5212\u8D28\u91CF\u7BA1\u7406</h3><p>\u89C4\u5212\u8D28\u91CF\u7BA1\u7406\u662F\u8BC6\u522B\u9879\u76EE\u53CA\u5176\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u8D28\u91CF\u8981\u6C42\u548C\uFF08\u6216\uFF09\u6807\u51C6, \u5E76\u4E66\u9762\u63CF\u8FF0\u9879\u76EE\u5C06\u5982\u4F55\u8BC1\u660E\u7B26\u5408\u8D28\u91CF\u8981\u6C42\u548C\uFF08\u6216\uFF09\u6807\u51C6\u7684\u8FC7\u7A0B\u3002</p><p>\u4E3B\u8981\u4F5C\u7528\u662F, \u4E3A\u5728\u6574\u4E2A\u9879\u76EE\u671F\u95F4\u5982\u4F55\u7BA1\u7406\u548C\u6838\u5B9E\u8D28\u91CF\u63D0\u4F9B\u6307\u5357\u548C\u65B9\u5411\u3002</p><h4 id="itto" tabindex="-1"><a class="header-anchor" href="#itto" aria-hidden="true">#</a> ITTO</h4><p>\u7EC4\u7EC7\u8FC7\u7A0B\u8D44\u4EA7 =&gt; \u6267\u884C\u7EC4\u7EC7\u7684\u8D28\u91CF\u653F\u7B56\u662F\u9AD8\u7EA7\u7BA1\u7406\u5C42\u6240\u63A8\u5D07\u7684, \u89C4\u5B9A\u4E86\u7EC4\u7EC7\u5728\u8D28\u91CF\u7BA1\u7406\u65B9\u9762\u7684\u5DE5\u4F5C\u3002</p><p>\u8D28\u91CF\u6210\u672C\uFF08\u4E0D\u5305\u542B\u751F\u4EA7\u6210\u672C\uFF09 =&gt; \u5728\u4EA7\u54C1\u751F\u547D\u5468\u671F\u4E2D\u4E3A\u9884\u9632\u4E0D\u7B26\u5408\u8981\u6C42, \u4E3A\u8BC4\u4EF7\u4EA7\u54C1\u6216\u670D\u52A1\u662F\u5426\u7B26\u5408\u8981\u6C42, \u4EE5\u53CA\u56E0\u672A\u8FBE\u5230\u8981\u6C42\uFF08\u8FD4\u5DE5\uFF09, \u800C\u53D1\u751F\u7684\u6240\u6709\u6210\u672C\u3002</p><ul><li>\u4E00\u81F4\u6027\u6210\u672C\uFF08\u4E3A\u4E86\u9632\u6B62\u5931\u8D25\uFF09 <ul><li>\u9884\u9632\u6210\u672C\uFF08\u57F9\u8BAD\u3001\u8BBE\u5907\u3001\u6D41\u7A0B\u6587\u6863\u5316\u3001\u9009\u62E9\u6B63\u786E\u65F6\u95F4\uFF09</li><li>\u8BC4\u4EF7\u6210\u672C\uFF08\u68C0\u67E5\u3001\u6D4B\u8BD5\u3001\u7834\u574F\u6027\u6D4B\u8BD5\uFF09</li></ul></li><li>\u975E\u4E00\u81F4\u6027\u6210\u672C\uFF08\u4E3A\u4E86\u5904\u7406\u5931\u8D25\uFF09 <ul><li>\u5185\u90E8\u5931\u8D25\u6210\u672C\uFF08\u8FD4\u5DE5\u3001\u5E9F\u54C1\uFF09</li><li>\u5916\u90E8\u5931\u8D25\u6210\u672C\uFF08\u503A\u52A1\u3001\u4FDD\u4FEE\u3001\u4E1A\u52A1\u6D41\u5931\uFF09</li></ul></li></ul><p>\u6D41\u7A0B\u56FE =&gt; \u53EF\u4EE5\u901A\u8FC7\u5206\u6790\u6D41\u7A0B\u56FE\u6765\u4F30\u7B97\u8D28\u91CF\u6210\u672C\u3002\u901A\u8FC7\u5C55\u793A\u8FC7\u7A0B\u6B65\u9AA4, \u53EF\u5E2E\u52A9\u6539\u8FDB\u8FC7\u7A0B\u5E76\u8BC6\u522B\u53EF\u80FD\u51FA\u73B0\u8D28\u91CF\u7F3A\u9677\u6216\u53EF\u4EE5\u7EB3\u5165\u8D28\u91CF\u68C0\u67E5\u7684\u5730\u65B9\u3002</p><p><img src="'+p+'" alt="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406-\u89C4\u5212\u8D28\u91CF\u7BA1\u7406"></p><h3 id="\u7BA1\u7406\u8D28\u91CF" tabindex="-1"><a class="header-anchor" href="#\u7BA1\u7406\u8D28\u91CF" aria-hidden="true">#</a> \u7BA1\u7406\u8D28\u91CF</h3><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u4E3B\u8981\u9488\u5BF9\u8FC7\u7A0B\u7684\u7BA1\u7406, \u8001\u7248\u672C\u4E2D\u53EB\u201C\u8D28\u91CF\u4FDD\u8BC1\u201D, QA\u3002\u4EE3\u8868\u7684\u5DE5\u5177\u4E3A\u8D28\u91CF\u5BA1\u8BA1, \u7528\u4E8E\u7BA1\u7406\u8FC7\u7A0B\u3002</p></div><p>\u7BA1\u7406\u8D28\u91CF\u662F\u628A\u7EC4\u7EC7\u7684\u8D28\u91CF\u653F\u7B56\u7528\u4E8E\u9879\u76EE, \u5E76\u5C06\u8D28\u91CF\u7BA1\u7406\u8BA1\u5212\u8F6C\u5316\u4E3A\u53EF\u6267\u884C\u7684\u8D28\u91CF\u6D3B\u52A8\u7684\u8FC7\u7A0B\u3002\u4E3B\u8981\u4F5C\u7528\u662F, \u63D0\u9AD8\u5B9E\u73B0\u8D28\u91CF\u76EE\u6807\u7684\u53EF\u80FD\u6027, \u4EE5\u53CA\u8BC6\u522B\u65E0\u6548\u8FC7\u7A0B\u548C\u5BFC\u81F4\u8D28\u91CF\u4F4E\u52A3\u7684\u539F\u56E0\u3002</p><p>\u5728\u654F\u6377\u9879\u76EE\u4E2D, \u6574\u4E2A\u9879\u76EE\u671F\u95F4\u7684\u8D28\u91CF\u7BA1\u7406\u7531\u6240\u6709\u56E2\u961F\u6210\u5458\u6267\u884C\uFF1B\u4F46\u5728\u4F20\u7EDF\u9879\u76EE\u4E2D, \u8D28\u91CF\u7BA1\u7406\u901A\u5E38\u662F\u7279\u5B9A\u56E2\u961F\u6210\u5458\u7684\u804C\u8D23\u3002</p><h4 id="itto-1" tabindex="-1"><a class="header-anchor" href="#itto-1" aria-hidden="true">#</a> ITTO</h4><ul><li>\u4EB2\u548C\u56FE\uFF1A\u53EF\u4EE5\u5BF9\u6F5C\u5728\u7F3A\u9677\u6210\u56E0\u8FDB\u884C\u5206\u7C7B, \u5C55\u793A\u6700\u5E94\u5173\u6CE8\u7684\u9886\u57DF\u3002</li><li>\u56E0\u679C\u56FE\uFF1A\u201C\u9C7C\u9AA8\u56FE\u201D\u3001\u201Cwhy-why \u5206\u6790\u56FE\u201D\u548C\u201C\u77F3\u5DDD\u56FE\u201D, \u627E\u6839\u672C\u539F\u56E0\u3002</li><li>\u76F4\u65B9\u56FE\uFF1A\u4E00\u79CD\u5C55\u793A\u6570\u5B57\u6570\u636E\u7684\u6761\u5F62\u56FE, \u53EF\u4EE5\u5C55\u793A\u6BCF\u4E2A\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u7F3A\u9677\u6570\u91CF\u3001\u7F3A\u9677\u6210\u56E0\u7684\u6392\u5217\u3001\u5404\u4E2A\u8FC7\u7A0B\u7684\u4E0D\u5408\u89C4\u6B21\u6570, \u6216\u9879\u76EE\u6216\u4EA7\u54C1\u7F3A\u9677\u7684\u5176\u4ED6\u8868\u73B0\u5F62\u5F0F\u3002</li><li>\u5E15\u7D2F\u6258\uFF1A\u4E8C\u516B\u539F\u5219, \u7D2F\u79EF\u9891\u7387, \u627E\u4E3B\u8981\u539F\u56E0, \u4FBF\u4E8E\u6709\u9488\u5BF9\u6027\u5730\u89E3\u51B3\u95EE\u9898\u3002</li><li>\u6563\u70B9\u56FE\uFF1A\u5C55\u793A\u4E24\u4E2A\u53D8\u91CF\u4E4B\u95F4\u7684\u5173\u7CFB\u7684\u56FE\u5F62\u3002</li><li>\uFF08\u8D28\u91CF\uFF09\u5BA1\u8BA1\uFF1A\u4E8E\u786E\u5B9A\u9879\u76EE\u6D3B\u52A8\u662F\u5426\u9075\u5FAA\u4E86\u7EC4\u7EC7\u548C\u9879\u76EE\u7684\u653F\u7B56\u3001\u8FC7\u7A0B\u4E0E\u7A0B\u5E8F\u7684\u4E00\u79CD\u7ED3\u6784\u5316\u4E14\u72EC\u7ACB\u7684\u8FC7\u7A0B\uFF1B\u5305\u62EC\uFF1A1 \u8BC6\u522B, 2 \u5206\u4EAB, 3 \u534F\u52A9, 4 \u79EF\u7D2F, 5 \u786E \u8BA4\uFF1B\u53EF\u4E8B\u5148\u5B89\u6392, \u4E5F\u53EF\u968F\u673A\u8FDB\u884C\uFF1B\u53EF\u7531\u5185\u90E8\u6216\u5916\u90E8\u5BA1\u8BA1\u5E08\u8FDB\u884C\u3002</li><li>\u9762\u5411 X \u7684\u8BBE\u8BA1 (DfX) \uFF1A\u4EA7\u54C1\u8BBE\u8BA1\u671F\u95F4\u53EF\u91C7\u7528\u7684\u4E00\u7CFB\u5217\u6280\u672F\u6307\u5357, \u65E8\u5728\u4F18\u5316\u8BBE\u8BA1\u7684\u7279\u5B9A\u65B9\u9762\u3002</li><li>\u95EE\u9898\u89E3\u51B3\uFF1A1 \u5B9A\u4E49, 2 \u8BC6\u522B, 3 \u65B9\u6848, 4 \u9009\u62E9, 5 \u6267\u884C, 6 \u9A8C\u8BC1\u3002</li><li>\u8D28\u91CF\u6539\u8FDB\u65B9\u6CD5\uFF1APDCA, \u516D\u897F\u683C\u739B\u3002</li><li>\u63A7\u5236\u56FE\uFF1A\u663E\u793A\u8FC7\u7A0B\u662F\u5426\u7A33\u5B9A\u3001\u662F\u5426\u6709\u53EF\u9884\u6D4B\u7684\u7EE9\u6548</li></ul><p>\u8D28\u91CF\u6D4B\u91CF\u6307\u6807 =&gt; \u63CF\u8FF0\u9879\u76EE\u6216\u4EA7\u54C1\u5C5E\u6027, \u4EE5\u53CA\u63A7\u5236\u8D28\u91CF\u8FC7\u7A0B\u5C06\u5982\u4F55\u9A8C\u8BC1\u7B26\u5408\u7A0B\u5EA6\u3002\uFF08\u5C5E\u4E8E\u201C\u9879\u76EE\u6587\u4EF6\u201C\uFF09</p><p>\u8D28\u91CF\u5BA1\u8BA1\u662F\u8BC6\u522B\u6539\u8FDB\u9886\u57DF\u7684\u5DE5\u5177\u4E4B\u4E00, \u662F\u4E00\u79CD\u72EC\u7ACB\u5BA1\u67E5\u3001\u786E\u4FDD\u9879\u76EE\u6267\u884C\u8FC7\u7A0B\u7B26\u5408\u7EC4\u7EC7\u6216\u9879\u76EE\u5B9A\u4E49\u7684\u65B9\u9488\u653F\u7B56\u3001\u6807\u51C6\u548C\u7A0B\u5E8F\u3002</p><p>\u8D28\u91CF\u5BA1\u8BA1\u7684\u76EE\u6807</p><ul><li>\u8BC6\u522B\u6700\u4F73\u5B9E\u8DF5\u3001\u8FDD\u89C4\u505A\u6CD5\u3001\u5DEE\u8DDD\u53CA\u4E0D\u8DB3</li><li>\u5206\u4EAB\u7C7B\u4F3C\u9879\u76EE\u7684\u826F\u597D\u5B9E\u8DF5</li><li>\u534F\u52A9\u8FC7\u7A0B\u6539\u8FDB\u3001\u63D0\u9AD8\u751F\u4EA7\u6548\u7387</li><li>\u79EF\u7D2F\u7ECF\u9A8C\u6559\u8BAD</li><li>\u786E\u8BA4\u5DF2\u6279\u51C6\u7684\u53D8\u66F4\u8BF7\u6C42\u7684\u5B9E\u65BD\u60C5\u51B5</li></ul><p>\u8FC7\u7A0B\u5206\u6790 =&gt; \u8BC6\u522B\u8FC7\u7A0B\u6539\u8FDB\u673A\u4F1A, \u53D1\u73B0\u4E00\u4E2A\u8FC7\u7A0B\u4E2D\u5B58\u5728\u7684\u95EE\u9898\u3001\u5236\u7EA6\u56E0\u7D20\u3001\u548C\u975E\u589E\u503C\u6D3B\u52A8\u3002</p><p><img src="'+s+'" alt="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406-\u7BA1\u7406\u8D28\u91CF"></p><h2 id="\u63A7\u5236\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u63A7\u5236\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u63A7\u5236\u8FC7\u7A0B\u7EC4</h2><h3 id="\u63A7\u5236\u8D28\u91CF" tabindex="-1"><a class="header-anchor" href="#\u63A7\u5236\u8D28\u91CF" aria-hidden="true">#</a> \u63A7\u5236\u8D28\u91CF</h3><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u4E3B\u8981\u9488\u5BF9\u7ED3\u679C\u7684\u68C0\u67E5, QC\u3002\u4EE3\u8868\u7684\u5DE5\u5177\u4E3A\u68C0\u67E5, \u6CF3\u8863\u68C0\u67E5\u7ED3\u679C\u3002</p></div><p>\u6838\u5B9E\u9879\u76EE\u53EF\u4EA4\u4ED8\u6210\u679C\u548C\u5DE5\u4F5C\u5DF2\u7ECF\u8FBE\u5230\u4E3B\u8981\u76F8\u5173\u65B9\u7684\u8D28\u91CF\u8981\u6C42, \u53EF\u4F9B\u6700\u7EC8\u9A8C\u6536\u3002</p><p>\u7531\u4E8E\u7F3A\u9677, \u5BA2\u6237\u62D2\u7EDD\u4EA4\u4ED8, \u82E5\u8981\u907F\u514D\u8FD9\u4E2A\u95EE\u9898, \u9879\u76EE\u7ECF\u7406\u4E8B\u5148\u5E94\u8BE5\u5B9A\u671F\u68C0\u67E5\u6838\u5B9E\u53EF\u4EA4\u4ED8\u6210\u679C\u3002</p><p>\u5728\u53EF\u4EA4\u4ED8\u6210\u679C\u4E2D\u53D1\u73B0\u5927\u91CF\u7F3A\u9677\uFF1A</p><ul><li>\u5C11\u91CF\u90E8\u5206 =&gt; \u67E5\u539F\u56E0\u3001\u8D70\u53D8\u66F4\u6D41\u7A0B</li><li>\u5927\u91CF\u90E8\u5206 =&gt; \u5BA1\u67E5\u8FC7\u7A0B\u3001\u8FC7\u7A0B\u6539\u8FDB</li></ul><p><img src="'+n+'" alt="\u9879\u76EE\u8D28\u91CF\u7BA1\u7406-\u63A7\u5236\u8D28\u91CF"></p>',43),d=[h];function o(u,m){return a(),t("div",null,d)}var f=i(c,[["render",o],["__file","project-quality-management.html.vue"]]);export{f as default};
