import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as i,d as t}from"./app.a3d402e1.js";var r="/assets/project-scope-management.png",s="/assets/project-scope-management-0.png",p="/assets/project-scope-management-1.png",n="/assets/project-scope-management-2.png",h="/assets/project-scope-management-3.png",c="/assets/project-scope-management-4.png",d="/assets/project-scope-management-5.png",l="/assets/project-scope-management-6.png";const o={},m=t('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u8BB0\u5F55\u9879\u76EE\u8303\u56F4\u7BA1\u7406\u7AE0\u8282\u7684\u76F8\u5173\u77E5\u8BC6\u3001\u9891\u7E41\u8003\u70B9\u3002</p></div><h2 id="\u6838\u5FC3\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u6982\u5FF5" aria-hidden="true">#</a> \u6838\u5FC3\u6982\u5FF5</h2><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u9879\u76EE\u8303\u56F4\u7BA1\u7406\u662F\u6307\u786E\u4FDD\u9879\u76EE\u5305\u62EC\u6210\u529F\u5B8C\u6210\u9879\u76EE\u6240\u9700\u7684\u529F\u5168\u90E8\u5DE5\u4F5C, \u4F46\u53C8\u53EA\u5305\u62EC\u5FC5\u987B\u5B8C\u6210\u7684\u5DE5\u4F5C\u7684\u5404\u4E2A\u8FC7\u7A0B, \u4E00\u53E5\u8BDD\u6982\u62EC\u4E3A\uFF1A\u505A\u4E14\u53EA\u505A\u8BE5\u505A\u7684\u4E8B, \u4E0D\u8BE5\u505A\u7684\u4E0D\u505A\u3002</p></div><ul><li>\u9879\u76EE\u8303\u56F4\u7BA1\u7406\u5305\u62EC\u505A\u4E14\u53EA\u505A\u6240\u9700\u7684\u5168\u90E8\u5DE5\u4F5C\u3002</li><li>\u201C\u8303\u56F4\u201D = \u4EA7\u54C1\u8303\u56F4+\u9879\u76EE\u8303\u56F4 <ul><li>\u4EA7\u54C1\u8303\u56F4 =&gt; \u67D0\u9879\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u6240\u5177\u6709\u7684\u7279\u6027\u548C\u529F\u80FD\uFF1B\u5B8C\u6210\u60C5\u51B5\u662F\u6839\u636E\u4EA7\u54C1\u9700\u6C42\u6765\u8861\u91CF\u7684</li><li>\u9879\u76EE\u8303\u56F4 =&gt; \u4E3A\u4EA4\u4ED8\u5177\u6709\u89C4\u5B9A\u7279\u6027\u4E0E\u529F\u80FD\u7684\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u800C\u5FC5\u987B\u5B8C\u6210\u7684\u5DE5\u4F5C\uFF1B\u5B8C\u6210\u60C5\u51B5\u662F\u6839\u636E\u9879\u76EE\u7BA1\u7406\u8BA1\u5212\u6765\u8861\u91CF\u7684</li></ul></li><li>\u9700\u6C42 <ul><li>\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u5FC5\u987B\u5177\u5907\u7684\u6761\u4EF6\u6216\u80FD\u529B</li><li>\u5DF2\u91CF\u5316\u4E14\u4E66\u9762\u8BB0\u5F55\u7684\u9700\u8981\u548C\u671F\u671B</li><li>\u9879\u76EE\u6267\u884C\u5F00\u59CB\u540E\u5BF9\u5176\u8FDB\u884C\u6D4B\u91CF</li><li>\u5DE5\u4F5C\u5206\u89E3\u7ED3\u6784\uFF08WBS\uFF09\u7684\u57FA\u7840</li><li>\u53D1\u8D77\u4EBA\u3001\u5BA2\u6237\u548C\u5176\u4ED6\u76F8\u5173\u65B9\u6536\u96C6\u5E76\u8861\u91CF</li></ul></li></ul><p>\u9879\u76EE\u8303\u56F4\u8BF4\u660E\u4E66\u7684\u7EC4\u6210\u90E8\u5206\uFF1A</p><p>\uFF081\uFF09\u4EA7\u54C1\u8303\u56F4\u63CF\u8FF0 \uFF082\uFF09\u9A8C\u6536\u6807\u51C6 \uFF083\uFF09\u53EF\u4EA4\u4ED8\u6210\u679C \uFF084\uFF09\u9664\u5916\u8D23\u4EFB</p><p>\u53EF\u4EA4\u4ED8\u6210\u679C\u548C\u9A8C\u6536\u6807\u51C6 =&gt; \u9996\u9009\u8303\u56F4\u8BF4\u660E\u4E66, \u6B21\u9009 WEB \u8BCD\u5178, \u518D\u6B21\u9009\u8303\u56F4\u57FA\u51C6</p><h2 id="\u601D\u7EF4\u5BFC\u56FE" tabindex="-1"><a class="header-anchor" href="#\u601D\u7EF4\u5BFC\u56FE" aria-hidden="true">#</a> \u601D\u7EF4\u5BFC\u56FE</h2><p><img src="'+r+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406"></p><h2 id="\u5982\u4F55\u505A\u597D\u9879\u76EE\u8303\u56F4\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u505A\u597D\u9879\u76EE\u8303\u56F4\u7BA1\u7406" aria-hidden="true">#</a> \u5982\u4F55\u505A\u597D\u9879\u76EE\u8303\u56F4\u7BA1\u7406</h2><p><img src="'+s+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406"></p><h2 id="\u89C4\u5212\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u89C4\u5212\u8FC7\u7A0B\u7EC4</h2><h3 id="\u89C4\u5212\u8303\u56F4\u7BA1\u7406" tabindex="-1"><a class="header-anchor" href="#\u89C4\u5212\u8303\u56F4\u7BA1\u7406" aria-hidden="true">#</a> \u89C4\u5212\u8303\u56F4\u7BA1\u7406</h3><ul><li>\u8303\u56F4\u7BA1\u7406\u8BA1\u5212\uFF08\u6307\u5357\uFF09\uFF1A\u63CF\u8FF0\u5982\u4F55\u5B9A\u4E49\u3001\u5236\u5B9A\u3001\u76D1\u7763\u3001\u63A7\u5236\u548C\u786E\u8BA4\u9879\u76EE\u8303\u56F4 <ul><li>\u8303\u56F4\u7BA1\u7406\u8BA1\u5212\u65E0\u8303\u56F4, \u8303\u56F4\u662F\u8BB0\u5F55\u5728\u8303\u56F4\u57FA\u51C6\u4E2D\u7684</li></ul></li><li>\u9700\u6C42\u7BA1\u7406\u8BA1\u5212\uFF1A\u63CF\u8FF0\u5982\u4F55\u5206\u6790\u3001\u8BB0\u5F55\u548C\u7BA1\u7406\u9879\u76EE\u548C\u4EA7\u54C1\u9700\u6C42 <ul><li>\u9700\u6C42\u7BA1\u7406\u8BA1\u5212\u65E0\u9700\u6C42, \u9700\u6C42\u662F\u8BB0\u5F55\u5728\u9700\u6C42\u6587\u4EF6\u4E2D\u7684</li></ul></li></ul><h4 id="itto" tabindex="-1"><a class="header-anchor" href="#itto" aria-hidden="true">#</a> ITTO</h4><p><img src="'+p+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u89C4\u5212\u8303\u56F4\u7BA1\u7406"></p><h3 id="\u6536\u96C6\u9700\u6C42" tabindex="-1"><a class="header-anchor" href="#\u6536\u96C6\u9700\u6C42" aria-hidden="true">#</a> \u6536\u96C6\u9700\u6C42</h3><p>\u9700\u6C42\u662F\u6307\u6839\u636E\u7279\u5B9A\u534F\u8BAE\u6216\u5176\u4ED6\u5F3A\u5236\u6027\u89C4\u8303, \u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u5FC5\u987B\u5177\u5907\u7684\u6761\u4EF6\u6216\u80FD\u529B\u3002</p><ul><li><p>\u9700\u6C42 =&gt; \u9700\u6C42\u662F\u4E00\u79CD\u9700\u8981</p></li><li><p>\u8303\u56F4 =&gt; \u8303\u56F4\u662F\u6EE1\u8DB3\u201C\u9700\u6C42\u201D\u5FC5\u987B\u4EA4\u4ED8\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u548C\u76F8\u5173\u5DE5\u4F5C</p></li><li><p>\u9700\u6C42\u6587\u4EF6\uFF1A\u9700\u6C42\u6587\u4EF6\u63CF\u8FF0\u5404\u79CD\u5355\u4E00\u9700\u6C42\u5C06\u5982\u4F55\u6EE1\u8DB3\u4E0E\u9879\u76EE\u76F8\u5173\u7684\u4E1A\u52A1\u9700\u6C42\u3002</p></li><li><p>\u9700\u6C42\u8DDF\u8E2A\u77E9\u9635\uFF1A\u9700\u6C42\u8DDF\u8E2A\u77E9\u9635\u662F\u628A\u4EA7\u54C1\u9700\u6C42\u4ECE\u5176\u6765\u6E90\u8FDE\u63A5\u5230\u80FD\u6EE1\u8DB3\u9700\u6C42\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u4E00\u79CD\u8868\u683C\u3002</p></li></ul><h4 id="itto-1" tabindex="-1"><a class="header-anchor" href="#itto-1" aria-hidden="true">#</a> ITTO</h4><p><img src="'+n+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u6536\u96C6\u9700\u6C42"></p><h3 id="\u5B9A\u4E49\u8303\u56F4" tabindex="-1"><a class="header-anchor" href="#\u5B9A\u4E49\u8303\u56F4" aria-hidden="true">#</a> \u5B9A\u4E49\u8303\u56F4</h3><p>\u5B9A\u4E49\u8303\u56F4\u662F\u5236\u5B9A\u9879\u76EE\u548C\u4EA7\u54C1\u8BE6\u7EC6\u63CF\u8FF0\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4E3B\u8981\u4F5C\u7528\u662F, \u63CF\u8FF0\u4EA7\u54C1\u3001\u670D\u52A1\u6216\u6210\u679C\u7684\u8FB9\u754C\u548C\u9A8C\u6536\u6807\u51C6\u3002\u53EF\u51CF\u5C11\u5BA2\u6237\u6539\u52A8\u8303\u56F4\u3002</p><p>\u9879\u76EE\u8303\u56F4\u8BF4\u660E\u4E66\uFF1A\u8BB0\u5F55\u4E86\u6574\u4E2A\u8303\u56F4, \u5305\u62EC\u9879\u76EE\u548C\u4EA7\u54C1\u8303\u56F4\uFF1B\u8BE6\u7EC6\u63CF\u8FF0\u4E86\u9879\u76EE\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\uFF1B\u8FD8\u4EE3\u8868\u9879\u76EE\u76F8\u5173\u65B9\u4E4B\u95F4\u5C31\u9879\u76EE\u8303\u56F4\u6240\u8FBE\u6210\u7684\u5171\u8BC6\u3002</p><h4 id="itto-2" tabindex="-1"><a class="header-anchor" href="#itto-2" aria-hidden="true">#</a> ITTO</h4><p><img src="'+h+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u5B9A\u4E49\u8303\u56F4"></p><h3 id="\u521B\u5EFA-wbs" tabindex="-1"><a class="header-anchor" href="#\u521B\u5EFA-wbs" aria-hidden="true">#</a> \u521B\u5EFA WBS</h3><p>\u628A\u9879\u76EE\u53EF\u4EA4\u4ED8\u6210\u679C\u548C\u9879\u76EE\u5DE5\u4F5C\u5206\u89E3\u6210\u8F83\u5C0F\u3001\u66F4\u6613\u4E8E\u7BA1\u7406\u7684\u7EC4\u4EF6\u7684\u8FC7\u7A0B\u3002</p><p>WBS \u7EC4\u7EC7\u5E76\u5B9A\u4E49\u4E86\u9879\u76EE\u7684\u603B\u8303\u56F4, \u4EE3\u8868\u7740\u7ECF\u6279\u51C6\u7684\u5F53\u524D\u9879\u76EE\u8303\u56F4\u8BF4\u660E\u4E66\u4E2D\u6240\u89C4\u5B9A\u7684\u5DE5\u4F5C\u3002WBS \u6700\u4F4E\u5C42\u7684\u7EC4\u6210\u90E8\u5206\u79F0\u4E3A\u5DE5\u4F5C\u5305\u3002</p><p>WBS \u6700\u4F4E\u5C42\u7684\u7EC4\u6210\u90E8\u5206\u79F0\u4E3A\u5DE5\u4F5C\u5305, \u5176\u4E2D\u5305\u62EC\u8BA1\u5212\u7684\u5DE5\u4F5C\u3002</p><p>\u9879\u76EE\u8303\u56F4\u8BF4\u660E\u4E66\uFF1A\u63CF\u8FF0\u4E86\u9700\u8981\u5B9E\u65BD\u7684\u5DE5\u4F5C\u53CA\u4E0D\u5305\u542B\u5728\u9879\u76EE\u4E2D\u7684\u5DE5\u4F5C\u3002</p><p>\u9700\u6C42\u6587\u4EF6\uFF1A\u9700\u6C42\u6587\u4EF6\u8BE6\u7EC6\u63CF\u8FF0\u4E86\u5404\u79CD\u5355\u4E00\u9700\u6C42\u5982\u4F55\u6EE1\u8DB3\u9879\u76EE\u7684\u4E1A\u52A1\u9700\u8981</p><p>\u8303\u56F4\u57FA\u51C6\uFF1A\u7ECF\u8FC7\u6279\u51C6\u7684\u8303\u56F4\u8BF4\u660E\u4E66\u3001WBS \u548C\u76F8\u5E94\u7684 WBS \u8BCD\u5178\u3002</p><h4 id="itto-3" tabindex="-1"><a class="header-anchor" href="#itto-3" aria-hidden="true">#</a> ITTO</h4><p><img src="'+c+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u521B\u5EFA WBS"></p><h2 id="\u76D1\u63A7\u8FC7\u7A0B\u7EC4" tabindex="-1"><a class="header-anchor" href="#\u76D1\u63A7\u8FC7\u7A0B\u7EC4" aria-hidden="true">#</a> \u76D1\u63A7\u8FC7\u7A0B\u7EC4</h2><h3 id="\u786E\u8BA4\u8303\u56F4" tabindex="-1"><a class="header-anchor" href="#\u786E\u8BA4\u8303\u56F4" aria-hidden="true">#</a> \u786E\u8BA4\u8303\u56F4</h3><p>\u786E\u8BA4\u8303\u56F4\u8FC7\u5173\u6CE8\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u9A8C\u6536, \u800C\u63A7\u5236\u8D28\u91CF\u5173\u6CE8\u53EF\u4EA4\u4ED8\u6210\u679C\u7684\u6B63\u786E\u6027\u53CA\u662F\u5426\u6EE1\u8DB3\u8D28\u91CF\u8981\u6C42\u3002\u63A7\u5236\u8D28\u91CF\u8FC7\u7A0B\u901A\u5E38\u5148\u4E8E\u786E\u8BA4\u8303\u56F4\u8FC7\u7A0B, \u4F46\u4E8C\u8005\u4E5F\u53EF\u540C\u65F6\u8FDB\u884C\u3002</p><p>\u6838\u5B9E\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\uFF1A\u6838\u5B9E\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u662F\u6307\u5DF2\u7ECF\u5B8C\u6210, \u5E76\u88AB\u63A7\u5236\u8D28\u91CF\u8FC7\u7A0B\u68C0\u67E5\u4E3A\u6B63\u786E\u7684\u53EF\u4EA4\u4ED8\u6210\u679C\u3002</p><p>\u786E\u8BA4\u9879\u76EE\u53EF\u4EA4\u4ED8\u6210\u679C\u662F\u5426\u7B26\u5408\u4EA7\u54C1\u9A8C\u6536\u6807\u51C6\u5E76\u6EE1\u8DB3\u5BA2\u6237\u7684\u671F\u671B\u3002</p><h4 id="itto-4" tabindex="-1"><a class="header-anchor" href="#itto-4" aria-hidden="true">#</a> ITTO</h4><p><img src="'+d+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u786E\u8BA4\u8303\u56F4"></p><h3 id="\u63A7\u5236\u8303\u56F4" tabindex="-1"><a class="header-anchor" href="#\u63A7\u5236\u8303\u56F4" aria-hidden="true">#</a> \u63A7\u5236\u8303\u56F4</h3><p>\u5C5E\u4E8E\u53D8\u66F4\u6D41\u7A0B\u3002</p><p>\u63A7\u5236\u8303\u56F4\u662F\u76D1\u7763\u9879\u76EE\u548C\u4EA7\u54C1\u7684\u8303\u56F4\u72B6\u6001, \u7BA1\u7406\u8303\u56F4\u57FA\u51C6\u53D8\u66F4\u7684\u8FC7\u7A0B\u3002\u672C\u8FC7\u7A0B\u7684\u4E3B\u8981\u4F5C\u7528\u662F, \u5728\u6574\u4E2A\u9879\u76EE\u671F\u95F4\u4FDD\u6301\u5BF9\u8303\u56F4\u57FA\u51C6\u7684\u7EF4\u62A4\u3002</p><h3 id="\u8303\u56F4\u8513\u5EF6\u3001\u9540\u91D1\u3001\u8303\u56F4\u6F5C\u53D8" tabindex="-1"><a class="header-anchor" href="#\u8303\u56F4\u8513\u5EF6\u3001\u9540\u91D1\u3001\u8303\u56F4\u6F5C\u53D8" aria-hidden="true">#</a> \u8303\u56F4\u8513\u5EF6\u3001\u9540\u91D1\u3001\u8303\u56F4\u6F5C\u53D8</h3><div class="custom-container tip"><p class="custom-container-title">\u8303\u56F4\u8513\u5EF6</p><ul><li>\u672A\u5BF9\u65F6\u95F4\u3001\u6210\u672C\u548C\u8D44\u6E90\u505A\u76F8\u5E94\u8C03\u6574\uFF0C\u672A\u7ECF\u63A7\u5236\u7684\u4EA7\u54C1\u6216\u9879\u76EE\u8303\u56F4\u7684\u6269\u5927\u3002</li><li>\u6765\u81EA\u56E2\u961F\u5185\u90E8\u539F\u56E0\u9020\u6210\u7684\u8303\u56F4\u8513\u5EF6\u79F0\u4E3A\u201C\u9540\u91D1\u201D\u3002</li><li>\u6765\u81EA\u56E2\u961F\u5916\u90E8\u539F\u56E0\u9020\u6210\u7684\u8303\u56F4\u8513\u5EF6\u79F0\u4E3A\u201C\u8303\u56F4\u6F5C\u53D8\u201D\u3002</li></ul></div><div class="custom-container tip"><p class="custom-container-title">\u9540\u91D1</p><p>\u9879\u76EE\u4EBA\u5458\u4E3A\u4E86\u201C\u8BA8\u597D\u201D\u5BA2\u6237\u800C\u505A\u7684\u4E0D\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u3001\u6CA1\u6709\u5E94\u7528\u4EF7\u503C\u7684\u9879\u76EE\u6D3B\u52A8\u3002</p></div><div class="custom-container tip"><p class="custom-container-title">\u8303\u56F4\u6F5C\u53D8</p><p>\u8303\u56F4\u6F5C\u53D8\u662F\u6307\u5BA2\u6237\u4E0D\u65AD\u63D0\u51FA\u5C0F\u7684\u3001\u4E0D\u6613\u5BDF\u89C9\u7684\u8303\u56F4\u6539\u53D8\uFF0C\u5982\u679C\u4E0D\u52A0\u63A7\u5236\uFF0C\u7D2F\u8BA1\u8D77\u6765\u5BFC\u81F4\u9879\u76EE\u4E25\u91CD\u504F\u79BB\u65E2\u5B9A\u7684\u8303\u56F4\u57FA\u51C6\uFF0C\u5BFC\u81F4\u9879\u76EE\u5931\u63A7\u548C\u5931\u8D25\u3002</p></div><p>\u5982\u679C\u5DF2\u7ECF\u51FA\u73B0\u4E86\u8303\u56F4\u8513\u5EF6\uFF0C\u9700\u8981\u505C\u6B62\u4E0D\u826F\u53D8\u66F4\uFF0C\u8865\u53D8\u66F4\u6D41\u7A0B\u3002\u5982\u679C\u53D8\u66F4\u6CA1\u6709\u83B7\u5F97\u6279\u51C6\uFF0C\u9700\u8981\u53D6\u6D88\u4E0D\u826F\u53D8\u66F4\u3002</p><h4 id="itto-5" tabindex="-1"><a class="header-anchor" href="#itto-5" aria-hidden="true">#</a> ITTO</h4><p><img src="'+l+'" alt="\u9879\u76EE\u8303\u56F4\u7BA1\u7406-\u786E\u8BA4\u8303\u56F4"></p>',52),u=[m];function _(g,f){return e(),i("div",null,u)}var b=a(o,[["render",_],["__file","project-scope-management.html.vue"]]);export{b as default};
