import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as r,d as e}from"./app.a3d402e1.js";const l={},t=e('<div class="custom-container tip"><p class="custom-container-title">\u524D\u8A00</p><p>\u654F\u6377\u5F00\u53D1\u8FC7\u7A0B\u91CD\u89C6\u56E2\u961F\u7684\u4EA4\u6D41\u4E0E\u7BA1\u7406\u95EE\u9898, \u5176\u63D0\u9AD8\u7814\u53D1\u6548\u7387\u7684\u663E\u8457\u6548\u679C, \u4F7F\u5F97\u654F\u6377\u5F00\u53D1\u9010\u6E10\u6210\u4E3A\u7EE7\u7011\u5E03\u5F0F\u5F00\u53D1\u4E4B\u540E, \u6700\u4E3A\u6D41\u884C\u7684\u8F6F\u4EF6\u5F00\u53D1\u65B9\u5F0F, \u5176\u4E2D, Scrum \u8FC7\u7A0B\u65E0\u7591\u662F\u76EE\u524D\u6700\u4E3A\u6210\u529F\u7684\u654F\u6377\u65B9\u6CD5\u3002</p><p>Scrum \u662F\u7528\u4E8E\u5F00\u53D1\u3001\u4EA4\u4ED8\u548C\u6301\u7EED\u652F\u6301\u590D\u6742\u4EA7\u54C1\u7684\u4E00\u4E2A\u6846\u67B6, \u662F\u4E00\u4E2A\u589E\u91CF\u7684\u3001\u8FED\u4EE3\u7684\u5F00\u53D1\u8FC7\u7A0B\u3002</p></div><h2 id="\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> \u57FA\u672C\u6982\u5FF5</h2><p>Scrum3355 \u6307\u7684\u662F\uFF1A3 \u4E2A\u6838\u5FC3\u89D2\u8272, 3 \u4E2A\u5DE5\u4EF6, 5 \u4E2A\u5173\u952E\u4E8B\u4EF6\u548C 5 \u4E2A\u4EF7\u503C\u89C2\u3002</p><p>Scrum \u662F\u4E00\u5957\u89E3\u51B3\u590D\u6742\u95EE\u9898\u7684\u6846\u67B6, \u8BA9\u6211\u4EEC\u4EE5\u8FED\u4EE3\u548C\u589E\u91CF\u7684\u65B9\u5F0F, \u5728\u6700\u77ED\u65F6\u95F4\u5185\u4EA4\u4ED8\u6700\u5927\u4EF7\u503C\u7684\u4EA7\u54C1\u3002\u5176\u4E2D\u7684\u6D41\u7A0B\u5E76\u975E\u4E00\u5B9A\u5B8C\u5168\u786C\u5957, \u5DE5\u5177\u662F\u6B7B\u7684, \u8981\u9002\u5F53\u7684\u6839\u636E\u5B9E\u9645\u60C5\u51B5\u4F5C\u51FA\u8C03\u6574, \u4EE5\u9002\u5408\u56E2\u961F\u7684\u6D41\u7A0B\u4E3A\u9996\u8981\u539F\u5219\u3002</p><p>\u654F\u6377\u4E5F\u662F\u4E00\u4E2A\u6301\u7EED\u4F18\u5316\u7684\u8FC7\u7A0B, \u901A\u8FC7\u6301\u7EED\u4EA4\u4ED8\u3001\u6301\u7EED\u4F18\u5316\u3001\u6301\u7EED\u6539\u8FDB\u3001\u6301\u7EED\u63D0\u5347\u3001\u6301\u7EED\u5851\u9020, \u6700\u7EC8\u5B9E\u73B0\u5C0F\u6B65\u5FEB\u8DD1, \u5FEB\u901F\u8FED\u4EE3\u3002</p><h2 id="_3-\u4E2A\u6838\u5FC3\u89D2\u8272" tabindex="-1"><a class="header-anchor" href="#_3-\u4E2A\u6838\u5FC3\u89D2\u8272" aria-hidden="true">#</a> 3 \u4E2A\u6838\u5FC3\u89D2\u8272</h2><h3 id="\u4EA7\u54C1\u8D1F\u8D23\u4EBA-po" tabindex="-1"><a class="header-anchor" href="#\u4EA7\u54C1\u8D1F\u8D23\u4EBA-po" aria-hidden="true">#</a> \u4EA7\u54C1\u8D1F\u8D23\u4EBA\uFF08PO\uFF09</h3><ul><li>\u8D1F\u8D23\u6700\u5927\u5316\u6295\u8D44\u56DE\u62A5\u7387\uFF08ROI\uFF09, \u901A\u8FC7\u786E\u5B9A\u4EA7\u54C1\u7279\u6027, \u628A\u5B83\u4EEC\u7FFB\u8BD1\u6210\u4E00\u4E2A\u6709\u4F18\u5148\u7EA7\u7684\u5217\u8868, \u4E3A\u4E0B\u4E00\u4E2A Sprint \u51B3\u5B9A\u5728\u8FD9\u4E2A\u5217\u8868\u4E2D\u54EA\u4E9B\u5E94\u5F53\u4F18\u5148\u7EA7\u6700\u9AD8, \u5E76\u4E14\u4E0D\u65AD\u5730\u91CD\u65B0\u8C03\u6574\u4F18\u5148\u7EA7\u548C\u68B3\u7406\u8FD9\u4E2A\u5217\u8868\u3002</li><li>\u804C\u8D23\u662F\u5B9A\u4E49\u9700\u6C42, \u5B9A\u4E49\u9700\u6C42\u4F18\u5148\u7EA7, \u5B9A\u4E49\u9700\u6C42\u7684\u9A8C\u6536\u6807\u51C6, \u5B9A\u4E49\u4EA7\u54C1\u53D1\u5E03\u5185\u5BB9\u4E0E\u65E5\u671F\u3002</li></ul><h3 id="scrum-master-\u654F\u6377\u6559\u7EC3" tabindex="-1"><a class="header-anchor" href="#scrum-master-\u654F\u6377\u6559\u7EC3" aria-hidden="true">#</a> Scrum Master/\u654F\u6377\u6559\u7EC3</h3><ul><li>\u5E2E\u52A9\u4EA7\u54C1\u5F00\u53D1\u56E2\u961F\u5B66\u4E60\u5E76\u5E94\u7528 Scrum \u6765\u8FBE\u6210\u5546\u4E1A\u4EF7\u503C, \u4E3A\u5927\u5BB6\u670D\u52A1, \u4F1A\u505A\u4EFB\u4F55\u529B\u6240\u80FD\u53CA\u7684\u4E8B\u60C5\u6765\u5E2E\u52A9\u56E2\u961F\u3001\u4EA7\u54C1\u8D1F\u8D23\u4EBA\u548C\u7EC4\u7EC7\u53D6\u5F97\u6210\u529F\u3002</li><li>\u5BF9\u5E94\u654F\u6377\u56E2\u961F\u4E2D\u7684\u9879\u76EE\u7ECF\u7406, \u4F46\u5E76\u975E\u662F\u4E00\u4E2A\u9879\u76EE\u7ECF\u7406\u3002\u804C\u8D23\u662F\u4FC3\u8FDB\u56E2\u961F\u7684\u5DE5\u4F5C, \u5E2E\u52A9\u56E2\u961F\u719F\u6089\u548C\u638C\u63E1\u654F\u6377\u7684\u4EF7\u503C\u89C2\u4E0E\u6846\u67B6, \u5E2E\u52A9\u6392\u9664\u5F71\u54CD\u751F\u4EA7\u529B\u969C\u788D, \u786E\u4FDD\u56E2\u961F\u4E0D\u53D7\u6253\u6270\u3002</li></ul><h3 id="\u5F00\u53D1\u56E2\u961F" tabindex="-1"><a class="header-anchor" href="#\u5F00\u53D1\u56E2\u961F" aria-hidden="true">#</a> \u5F00\u53D1\u56E2\u961F</h3><ul><li>\u5EFA\u9020\u4EA7\u54C1\u8D1F\u8D23\u4EBA\u6240\u6307\u5B9A\u7684\u4EA7\u54C1\u3002\u5BF9\u4EA4\u4ED8\u7ED3\u679C\u8D1F\u8D23\u3002</li><li>\u56E2\u961F\u662F\u201C\u8DE8\u804C\u80FD\u201D\u7684, \u5B83\u5305\u542B\u4E86\u6240\u6709\u4E13\u4E1A\u80FD\u529B, \u5982\u5F00\u53D1\u3001\u6D4B\u8BD5\u3001\u9700\u6C42\u5206\u6790\u7B49, \u5E76\u4E14\u5B83\u662F\u201C\u81EA\u7EC4\u7EC7\u201D[\u81EA\u7BA1\u7406]\u7684, \u88AB\u7ED9\u4E88\u5F88\u9AD8\u7A0B\u5EA6\u7684\u81EA\u6CBB\u548C\u8D23\u4EFB\u3002</li></ul><h2 id="_3-\u4E2A\u5DE5\u4EF6" tabindex="-1"><a class="header-anchor" href="#_3-\u4E2A\u5DE5\u4EF6" aria-hidden="true">#</a> 3 \u4E2A\u5DE5\u4EF6</h2><h3 id="\u4EA7\u54C1\u4EE3\u529E\u4E8B\u9879-product-backiog" tabindex="-1"><a class="header-anchor" href="#\u4EA7\u54C1\u4EE3\u529E\u4E8B\u9879-product-backiog" aria-hidden="true">#</a> \u4EA7\u54C1\u4EE3\u529E\u4E8B\u9879\uFF08Product BackIog\uFF09</h3><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u4EA7\u54C1\u4EE3\u529E\u4E8B\u9879, \u5373\u4EA7\u54C1\u89C6\u89D2\u7684\u9700\u6C42\u6E05\u5355\u3002</p></div><ul><li>\u7531 Product Owner \u8D1F\u8D23\u7EF4\u62A4, \u5305\u62EC\u589E\u5220\u53CA\u4F18\u5148\u7EA7\u6392\u5E8F</li><li>\u7528\u6237\u6545\u4E8B\u662F\u5176\u4E2D\u4E00\u79CD\u6700\u4F73\u5B9E\u8DF5</li><li>\u6BCF\u9879\u9700\u6C42\u90FD\u9700\u8981\u63CF\u8FF0\u5176\u5916\u90E8\u4EF7\u503C</li></ul><h3 id="sprint-\u8FED\u4EE3\u4EE3\u529E\u6E05\u5355" tabindex="-1"><a class="header-anchor" href="#sprint-\u8FED\u4EE3\u4EE3\u529E\u6E05\u5355" aria-hidden="true">#</a> Sprint \u8FED\u4EE3\u4EE3\u529E\u6E05\u5355</h3><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>Sprint \u8FED\u4EE3\u4EE3\u529E\u6E05\u5355, \u5373\u6B64\u6B21\u51B2\u523A\u5468\u671F\u5185\u89C4\u5212\u8981\u5B8C\u6210\u7684\u5185\u5BB9\u3002</p></div><ul><li>\u6765\u6E90\u4E8E Product Backlog</li><li>\u7531\u56E2\u961F\u8BC4\u4F30\u548C\u9009\u62E9 Product BackIog \u4E2D\u54EA\u4E9B\u653E\u5165 Sprint BackIog</li><li>\u56E2\u961F\u9700\u8981\u4E00\u8D77\u5B9A\u4E49\u201C\u5B8C\u6210\u201D\u6807\u51C6</li></ul><h3 id="\u6F5C\u5728\u53EF\u53D1\u4EA7\u54C1\u589E\u91CF-increment" tabindex="-1"><a class="header-anchor" href="#\u6F5C\u5728\u53EF\u53D1\u4EA7\u54C1\u589E\u91CF-increment" aria-hidden="true">#</a> \u6F5C\u5728\u53EF\u53D1\u4EA7\u54C1\u589E\u91CF\uFF08Increment\uFF09</h3><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>\u53EF\u4EA4\u4ED8\u4EA7\u54C1\u589E\u91CF\uFF08Increment\uFF09, \u5373\u51B2\u523A\u7ED3\u675F\u540E\u53EF\u5BF9\u5916\u53D1\u5E03\u7684\u4EA7\u54C1\u529F\u80FD\u589E\u91CF\u90E8\u5206\u3002</p></div><ul><li>\u9700\u8981\u5173\u6CE8\u5176\u662F\u53EF\u5DE5\u4F5C\u7684\u8F6F\u4EF6\u529F\u80FD\u589E\u91CF</li><li>\u9700\u8981\u5728 Scrum Review \u4F1A\u8BAE\u4E0A\u8FDB\u884C\u5C55\u793A</li></ul><h2 id="_5-\u4E2A\u5173\u952E\u4E8B\u4EF6" tabindex="-1"><a class="header-anchor" href="#_5-\u4E2A\u5173\u952E\u4E8B\u4EF6" aria-hidden="true">#</a> 5 \u4E2A\u5173\u952E\u4E8B\u4EF6</h2><h3 id="\u51B2\u523A-sprint" tabindex="-1"><a class="header-anchor" href="#\u51B2\u523A-sprint" aria-hidden="true">#</a> \u51B2\u523A\uFF08Sprint\uFF09</h3><ul><li>\u51B2\u523A Sprint \u6216\u8FED\u4EE3\u662F\u4E00\u4E2A\u7279\u6B8A\u7684\u4E8B\u4EF6, \u6216\u8005\u8BF4\u5176\u4E00\u4E2A\u5BB9\u5668\u4E8B\u4EF6\u3002\u540E\u7EED\u56DB\u4E2A\u4E8B\u4EF6\u5305\u542B\u5728\u5176\u4E2D\u3002</li><li>2~4 \u5468, \u56FA\u5B9A\u5468\u671F, \u56FA\u5B9A\u4E8B\u4EF6\u5F00\u59CB, \u56FA\u5B9A\u4E8B\u4EF6\u7ED3\u675F\u3002\u65F6\u95F4\u76D2\u662F\u5176\u4E00\u4E2A\u91CD\u8981\u6982\u5FF5\u3002</li></ul><h3 id="sprint-\u8BA1\u5212\u4F1A" tabindex="-1"><a class="header-anchor" href="#sprint-\u8BA1\u5212\u4F1A" aria-hidden="true">#</a> Sprint \u8BA1\u5212\u4F1A</h3><ul><li>Sprint \u89C4\u5212\u4F1A\u7684\u6838\u5FC3\u8BAE\u9898\u662F\u4E0B\u4E00\u6B21\u51B2\u523A\u8981\u5B9E\u73B0\u7684\u76EE\u6807\u548C\u8303\u56F4\u3002</li><li>\u4ECE Product Backlog \u4E2D\u9009\u53D6\u9AD8\u4F18\u5148\u7EA7\u7684\u9700\u6C42, \u786E\u5B9A Sprint \u7684\u76EE\u6807, \u5BF9\u4EA7\u54C1 backIog \u4E2D\u6545\u4E8B\u8FDB\u884C\u4F30\u7B97, \u4EE5\u4F5C\u4E3A\u662F\u5426\u653E\u5165\u4E0B\u671F\u7684\u53C2\u8003\u3002\u5BF9\u4E8E\u9700\u6C42\u4E0D\u6E05\u695A\u7684\u6545\u4E8B, \u9700\u8981\u4EA7\u54C1\u8D1F\u8D23\u4EBA\u8FDB\u884C\u8BF4\u660E\u3002</li><li>\u4F1A\u8BAE\u4E2D\u8F93\u5165\u662F Product backIog, \u8F93\u51FA\u662F Sprint BackIog\u3002</li></ul><h3 id="\u6BCF\u65E5-scrum-\u7AD9\u4F1A" tabindex="-1"><a class="header-anchor" href="#\u6BCF\u65E5-scrum-\u7AD9\u4F1A" aria-hidden="true">#</a> \u6BCF\u65E5 Scrum \u7AD9\u4F1A</h3><ul><li>\u7AD9\u4F1A\u7684\u76EE\u6807\u662F\u4FC3\u8FDB\u4FE1\u606F\u5728\u56E2\u961F\u5185\u5171\u4EAB\u4E0E\u900F\u660E\u3002\u6BCF\u6B21 15 \u5206\u949F\u5DE6\u53F3, \u4E0D\u5BF9\u95EE\u9898\u8FDB\u884C\u6DF1\u5165\u8BA8\u8BBA, \u6BCF\u5929\u56FA\u5B9A\u65F6\u95F4\u53EC\u5F00\u3002</li><li>\u56E2\u961F\u6210\u5458\u9700\u8981\u56DE\u7B54 3 \u4E2A\u95EE\u9898\uFF1A <ul><li>\u6628\u5929\u6211\u505A\u4E86\u54EA\u4E9B\u4E8B\u60C5</li><li>\u4ECA\u5929\u8BA1\u5212\u8981\u505A\u4EC0\u4E48\u4E8B\u60C5</li><li>\u662F\u5426\u9047\u5230\u95EE\u9898, \u963B\u788D\u8FBE\u6210\u76EE\u6807</li></ul></li></ul><h3 id="sprint-\u8BC4\u5BA1\u4F1A" tabindex="-1"><a class="header-anchor" href="#sprint-\u8BC4\u5BA1\u4F1A" aria-hidden="true">#</a> Sprint \u8BC4\u5BA1\u4F1A</h3><p>\u8BC4\u5BA1\u4F1A\u5728\u51B2\u523A\u9ED8\u5951\u53EC\u5F00, \u68C0\u67E5\u672C\u671F\u7684\u6210\u679C, \u9700\u8981\u56E2\u961F\u5168\u5458\u53C2\u4E0E, \u5E76\u9080\u8BF7\u4EA7\u54C1\u76F8\u5173\u5E72\u7CFB\u4EBA\u5BF9\u4EA7\u54C1\u8FDB\u884C\u5C55\u793A, \u82E5\u4E0E\u4EA7\u54C1\u8D1F\u8D23\u4EBA\u9884\u60F3\u7684\u4E0D\u4E00\u6837, \u4EA7\u54C1\u8D1F\u8D23\u4EBA\u53EF\u4EE5\u62D2\u7EDD\u63A5\u6536\u6210\u679C\u3002</p><h3 id="sprint-\u56DE\u987E\u4F1A" tabindex="-1"><a class="header-anchor" href="#sprint-\u56DE\u987E\u4F1A" aria-hidden="true">#</a> Sprint \u56DE\u987E\u4F1A</h3><p>\u51B2\u523A\u7ED3\u675F\u540E, \u56E2\u961F\u4E00\u8D77\u590D\u76D8\u672C\u6B21\u51B2\u523A\u7684\u8FC7\u7A0B, \u603B\u7ED3\u7ECF\u9A8C\u4E0E\u6559\u8BAD, \u5E76\u5F62\u6210\u5207\u5B9E\u53EF\u884C\u7684\u6539\u8FDB\u6E05\u5355\u3002</p><h2 id="_5-\u4E2A\u4EF7\u503C\u89C2" tabindex="-1"><a class="header-anchor" href="#_5-\u4E2A\u4EF7\u503C\u89C2" aria-hidden="true">#</a> 5 \u4E2A\u4EF7\u503C\u89C2</h2><ul><li>\u3010\u5F00\u653E\u3011- Scrum \u628A\u9879\u76EE\u4E2D\u7684\u4E00\u5207\u5F00\u653E\u7ED9\u6BCF\u4E2A\u4EBA\u770B</li><li>\u3010\u5C0A\u91CD\u3011- \u6BCF\u4E2A\u4EBA\u90FD\u6709\u4ED6\u72EC\u7279\u7684\u80CC\u666F\u548C\u7ECF\u9A8C</li><li>\u3010\u52C7\u6C14\u3011- \u6709\u52C7\u6C14\u505A\u51FA\u627F\u8BFA, \u5C65\u884C\u627F\u8BFA, \u63A5\u53D7\u522B\u4EBA\u7684\u5C0A\u91CD</li><li>\u3010\u4E13\u6CE8\u3011- \u628A\u4F60\u7684\u5FC3\u601D\u548C\u80FD\u529B\u90FD\u7528\u5230\u4F60\u627F\u8BFA\u7684\u5DE5\u4F5C\u4E0A\u53BB</li><li>\u3010\u627F\u8BFA\u3011- \u613F\u610F\u5BF9\u76EE\u6807\u505A\u51FA\u627F\u8BFA, \u5168\u8EAB\u5FC3\u6295\u5165\u53BB\u5B8C\u6210 Scrum \u56E2\u961F\u7684\u76EE\u6807, \u800C\u4E0D\u662F\u5FC5\u987B\u6309\u8BA1\u5212\u5B8C\u6210, \u4E24\u8005\u4E4B\u95F4\u662F\u6709\u533A\u522B\u7684\u3002</li></ul>',35),c=[t];function n(d,h){return a(),r("div",null,c)}var o=i(l,[["render",n],["__file","scrum-3355.html.vue"]]);export{o as default};
