import{_ as i}from"./plugin-vue_export-helper-c27b6911.js";import{o as a,c as e,e as l}from"./app-89f8fb1f.js";const h={},r=l('<h2 id="瀑布模型" tabindex="-1"><a class="header-anchor" href="#瀑布模型" aria-hidden="true">#</a> 瀑布模型</h2><h3 id="特点" tabindex="-1"><a class="header-anchor" href="#特点" aria-hidden="true">#</a> 特点</h3><ul><li>严格区分阶段，每个阶段因果关系紧密相连</li><li>只适合需求明确的项目</li></ul><h3 id="缺点" tabindex="-1"><a class="header-anchor" href="#缺点" aria-hidden="true">#</a> 缺点</h3><ul><li>软件需求完整性、正确性难确定</li><li>严格串行化，很长时间才能看到结果</li><li>瀑布模型要求每个阶段一次性完全解决该阶段工作，这不现实。</li></ul><h3 id="阶段" tabindex="-1"><a class="header-anchor" href="#阶段" aria-hidden="true">#</a> 阶段</h3><ul><li>需求分析</li><li>软件设计</li><li>程序设计</li><li>编码实现</li><li>单元测试</li><li>集成测试</li><li>系统测试</li><li>运行维护</li></ul><h2 id="原型模型" tabindex="-1"><a class="header-anchor" href="#原型模型" aria-hidden="true">#</a> 原型模型</h2><h3 id="分类" tabindex="-1"><a class="header-anchor" href="#分类" aria-hidden="true">#</a> 分类</h3><ul><li>抛弃型原型</li><li>演化型原型</li></ul><h3 id="阶段-1" tabindex="-1"><a class="header-anchor" href="#阶段-1" aria-hidden="true">#</a> 阶段</h3><ul><li>原型开发</li><li>目标软件开发</li></ul><h2 id="v-模型" tabindex="-1"><a class="header-anchor" href="#v-模型" aria-hidden="true">#</a> V 模型</h2><h3 id="特点-1" tabindex="-1"><a class="header-anchor" href="#特点-1" aria-hidden="true">#</a> 特点</h3><ul><li>测试贯穿于始终</li><li>测试分阶段，测试计划提前</li></ul><h3 id="阶段-2" tabindex="-1"><a class="header-anchor" href="#阶段-2" aria-hidden="true">#</a> 阶段</h3><ul><li>需求分析 =&gt; 验收测试</li><li>系统设计 =&gt; 系统测试</li><li>详细设计 =&gt; 集成测试</li><li>编码 =&gt; 单元测试</li></ul><h2 id="螺旋模型" tabindex="-1"><a class="header-anchor" href="#螺旋模型" aria-hidden="true">#</a> 螺旋模型</h2><h3 id="特点-2" tabindex="-1"><a class="header-anchor" href="#特点-2" aria-hidden="true">#</a> 特点</h3><ul><li>以快速原型为基础＋瀑布模型; 考虑了风险问题</li><li>风险分析: 评价方案、识别风险、消除风险</li><li>目标设定: 决定目标、方案和限制</li></ul><h2 id="构件组装模型" tabindex="-1"><a class="header-anchor" href="#构件组装模型" aria-hidden="true">#</a> 构件组装模型</h2><h3 id="特点-3" tabindex="-1"><a class="header-anchor" href="#特点-3" aria-hidden="true">#</a> 特点</h3><ul><li>易扩展、易重用、降低成本、安排任务更灵活。</li></ul><h3 id="缺点-1" tabindex="-1"><a class="header-anchor" href="#缺点-1" aria-hidden="true">#</a> 缺点</h3><ul><li>构件设计要求经验丰富的架构师、设计不好的构件难重用、强调重用可能牺牲其它指标（如性能）、第三方构件质量难控制。</li></ul><h3 id="阶段-3" tabindex="-1"><a class="header-anchor" href="#阶段-3" aria-hidden="true">#</a> 阶段</h3><ul><li>需求分析和定义</li><li>设计构件组装</li><li>建立构件库</li><li>构建应用软件</li><li>测试和发布</li></ul><h2 id="快速应用开发模型-rad" tabindex="-1"><a class="header-anchor" href="#快速应用开发模型-rad" aria-hidden="true">#</a> 快速应用开发模型（RAD</h2><h3 id="特点-4" tabindex="-1"><a class="header-anchor" href="#特点-4" aria-hidden="true">#</a> 特点</h3><ul><li>业务建模</li><li>数据建模</li><li>过程建模</li><li>应用生成</li><li>测试与交付</li></ul>',30),d=[r];function n(t,c){return a(),e("div",null,d)}const o=i(h,[["render",n],["__file","software-engineering.html.vue"]]);export{o as default};
