import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as n,c as l,d as a,a as i,e}from"./app-8633ea60.js";const c={},s=i("div",{class:"hint-container tip"},[i("p",{class:"hint-container-title"},"前言"),i("p",null,"PMP 全书中出现了不少的工具与技术, 本文针对这些工具技术进行分类整理和并且对于这些名词做出相应的解释。")],-1),p=e('<h2 id="数据收集" tabindex="-1"><a class="header-anchor" href="#数据收集" aria-hidden="true">#</a> 数据收集</h2><div class="hint-container tip"><p class="hint-container-title">头脑风暴</p><ul><li>表示在短时间内获得大量创意, 适用于团队环境, 需要引导者进行引导头脑风暴由两部分构成：创意产生和创意分析。</li><li>头脑风暴简单的理解为就是畅所欲言不批评和评价。</li><li>畅所欲言、只求数量, 不求质量。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">焦点小组</p><ul><li>召集相关方和主体专家讨论项目风险, 成功标准和其他议题。</li><li>一般是同一个领域, 同一个部门的人员(同职能)。</li><li>由 2 个或 2 个以上的人在会议室开会讨论, 其中有一个主持人进行主持, 并且只围绕一个主题进行焦点性讨论。</li><li>比一对一访谈更有利于互动交流。</li><li>主持人引导互动式讨论。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">访谈</p><ul><li>直接面对面一对一的交谈, 具体是指通过与相关方直接交谈了解高层级需求, 假设条件, 制约因素, 审批标准以及其他信息。</li><li>预设和即兴问题、一对一、多对多、获取机密信息。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">问卷调查</p><ul><li>受众多样、需快速完成、地理位置分散、适合开展统计分析。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">标杆对照</p><ul><li>标杆可以是内部或外部、同行业或者不同行业、识别最佳实践、形成改进意见。</li></ul></div><h2 id="数据分析" tabindex="-1"><a class="header-anchor" href="#数据分析" aria-hidden="true">#</a> 数据分析</h2><div class="hint-container tip"><p class="hint-container-title">挣值分析</p><p>对范围、进度、成本绩效进行综合分析, 发现偏差。</p></div><div class="hint-container tip"><p class="hint-container-title">根本原因分析</p><p>寻找偏差或潜在问题的根本原因。</p></div><div class="hint-container tip"><p class="hint-container-title">成本效益分析</p><p>选择成本最低的方案来纠偏。</p></div><div class="hint-container tip"><p class="hint-container-title">参数估算</p><p>利用历史数据之间的统计关系和其他变量来估算, 回归分析是典型的参数估算。</p></div><div class="hint-container tip"><p class="hint-container-title">三点估算</p><p>考虑不确定性与风险, 提高估算准确性。</p></div><h2 id="决策" tabindex="-1"><a class="header-anchor" href="#决策" aria-hidden="true">#</a> 决策</h2><div class="hint-container tip"><p class="hint-container-title">一致同意</p><p>每个人都同意、德尔菲(专家、匿名、多轮、趋同、消除意见)。</p></div><div class="hint-container tip"><p class="hint-container-title">大多数同意</p><p>超过 50%, 一般把决策小组的人数定为奇数。</p></div><div class="hint-container tip"><p class="hint-container-title">相对多数同意</p><p>相对多数, 通常候选项超过两个时使用。</p></div><h2 id="数据表现" tabindex="-1"><a class="header-anchor" href="#数据表现" aria-hidden="true">#</a> 数据表现</h2><div class="hint-container tip"><p class="hint-container-title">核对单</p><ul><li>很多组织基于自身经验制定了标准化的核对单, 或者采用所在行业的核对单。</li><li>包括需要考虑的项目, 行动或要点的清单, 常被用作提醒。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">因果图（鱼刺图）</p><p>显示各种因素对问题结果造成的影响, 用来找出全部原因。</p><ul><li>关键词：找出所有原因</li><li>着眼于刺激思考</li><li>探索理想的结果</li></ul></div><div class="hint-container tip"><p class="hint-container-title">控制图（过程控制图）</p><p>确定某个过程是否稳定是否在控制之内。</p><ul><li>关键词：针对过程</li><li>确定某流程是否正常运转</li></ul></div><div class="hint-container tip"><p class="hint-container-title">流程图</p><p>帮助分析问题, 显示系统各组成部分之间的逻辑关系。</p><ul><li>关键词：找原因</li></ul></div><div class="hint-container tip"><p class="hint-container-title">直方图</p><p>显示各变量的分布, 每一栏代表一种变量, 直观显示各变量所占比例。</p></div><div class="hint-container tip"><p class="hint-container-title">帕累托图</p><p>特殊的直方图, 按照发生频率大小顺序排列, 表示有多少结果是由已确认的类型的原因引起的。</p><ul><li>关键词：用于识别造成大多数问题的少数重要原因</li><li>着眼于改进质量最重要的问题上</li></ul></div><div class="hint-container tip"><p class="hint-container-title">趋势图</p><p>反映一个过程在某一段时间的趋势。</p><ul><li>关键词：用来预测未来</li></ul></div><div class="hint-container tip"><p class="hint-container-title">散点图</p><p>显示两个变量之间的关系和规律(因变量 Y 相对于自变量 X 的变化)。</p></div><div class="hint-container tip"><p class="hint-container-title">思维导图</p><ul><li>把从头脑风暴中获得的创意整合成一张图, 以反映创意之间的共性与差异, 激发新创意。</li><li>整合、反映共性与差异、激发新创意、脑图。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">亲和图</p><p>分组、分类。对大量创意进行分组, 以便进一步审查和分析。可以对潜在缺陷成因进行分类, 展示最应关注的领域。</p></div><h2 id="人际关系与团队技能" tabindex="-1"><a class="header-anchor" href="#人际关系与团队技能" aria-hidden="true">#</a> 人际关系与团队技能</h2><div class="hint-container tip"><p class="hint-container-title">名义小组</p><p>促进头脑风暴、投票、优先排序、5 分制、数轮。</p></div><div class="hint-container tip"><p class="hint-container-title">引导</p><p>与主题研讨会结合使用、跨职能、不同部门、协调相关方差异。</p></div><div class="hint-container tip"><p class="hint-container-title">观察(工作跟随)和交谈</p><p>“工作跟随”、难以或不愿清晰说明、挖掘隐藏的需求。</p></div><div class="hint-container tip"><p class="hint-container-title">冲突管理</p><ul><li>有助于相关方就目标、成功标准、高层级需求、项目描述、总体里程碑等内容达成一致意见。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">引导</p><ul><li>是指有效引导团队活动成功以达成决定, 解决方案或结论的能力。</li><li>引导者确保参与者有效参与, 互相理解, 考虑所有意见, 按既定决策流程全力支持得到的结论或结果。</li></ul></div><div class="hint-container tip"><p class="hint-container-title">会议管理</p><ul><li>不能把各种会议类型混合在一起, 面对面的会议效果是最好的, 根据情况可以举行虚拟会议, 确保有效参与会议, 会议要达成共识, 要有行动计划。</li><li>会前, 确保会议议程, 主题, 目的和目标和期限。</li><li>会中, 不跑题。</li><li>会后, 要有书面的会议纪要和行动方案</li></ul></div><div class="hint-container tip"><p class="hint-container-title">X 理论</p><p>（1）员工天生不喜欢工作,只要可能,就会逃避工作。<br> （2）由于员工不喜欢工作,因此必须对他们采取强制的措施或惩罚的办法,迫使他们去 实现组织的目标。<br> （3）员工只要有可能就会逃避责任,安于现状。<br> （4）大多数员工喜欢安逸,没有雄心壮志。</p></div><div class="hint-container tip"><p class="hint-container-title">Y 理论</p><p>（1）员工视工作如休息、娱乐一般自然。<br> （2）如果员工对某项工作做出承诺,他们会进行自我指导和自我控制,以完成任务。<br> （3）一般而言,每个人不仅能够承担责任,而且会主动寻求承担责任。<br> （4）绝大多数人都具备做出正确决策的能力,而不仅仅管理者具备这一能力。</p></div>',36);function r(h,d){return n(),l("div",null,[s,a(" more "),p])}const u=t(c,[["render",r],["__file","toolsand-techniques.html.vue"]]);export{u as default};
